import React, { useState, useEffect } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import stateWrapper from "../../containers/provider";
import instance from "../../helpers/axiosly";

//i18n
import { withNamespaces } from "react-i18next";
import moment from "moment";

let countInit = 1; 

const OrderList = (props) => {
  const [state, setState] = useState({
    orderList: [],
    loading: false
  });



  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/order`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })

        let orders = res.data.data.rows
        setState({
          ...state,
          orderList: orders,
          loading: false
        });
    } catch (e) {
      console.log(e);
    }
    }
    fetchData();

    //eslint-disable-line
  }, [])






  //useEffect(() => {
   // let orderId, sessionData, topic, dateCreated, total, balance;

   // sessionData = props.orderStore.state.sessionData.orderList;
   // sessionData.map((obj, id) => {
     // console.log("get order id for list", obj.id);
    //});

    // return () =>{
    //   setState({
    //     ...state,
    //     orderList: sessionData,
    //   });
    // }
  //}, []);

  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <CardTitle className="mb-4">RECENT ORDERS</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):
                  (
                    <div className="table-responsive">
                    <table className="table table-centered table-nowrap mb-0">
                      <thead className="thead-light">
                        <tr>
                          <th style={{ width: "20px" }}>
                            <div className="custom-control custom-checkbox">
                              <input
                                type="checkbox"
                                className="custom-control-input"
                                id="customCheck1"
                              />
                              <label
                                className="custom-control-label"
                                htmlFor="customCheck1"
                              >
                                &nbsp;
                              </label>
                            </div>
                          </th>
                          <th>S/N</th>
                          <th>Topic</th>
                          <th>Date</th>
                          <th>B</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {state.orderList.map((list, key) => (
                          <tr key={"_tr_" + key}>
                            <td>
                              <div className="custom-control custom-checkbox">
                                <input
                                  type="checkbox"
                                  className="custom-control-input"
                                  id={list.id}
                                />
                                {/* ({let total =  list.amount + list.balance}) */}

                                <label
                                  className="custom-control-label"
                                  htmlFor={list.id}
                                >
                                  &nbsp;
                                </label>
                              </div>
                            </td>
                            <td>
                              <Link to="#" className="text-body font-weight-bold">
                                {" "}
                                {countInit++}{" "}
                              </Link>{" "}
                            </td>
                            <td>{list.topic}</td>
                            <td>{moment(list.createdAt).format("MMMM Do YYYY")}</td>
                            <td>{`$${list.balance}`}</td>
                            <td>
                              <Link
                                className="btn btn-primary waves-effect waves-light"
                                to="/"
                              >
                                view
                              </Link>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                  )
                  }


        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(OrderList));
