import React, { useState, useEffect } from "react";
import {
    Container,
    Row,
    Col,
    Button,
    Card,
    CardBody,
    NavItem,
    NavLink,
    TabContent,
    TabPane,
    FormGroup,
    Label,
    Input,
} from "reactstrap";
import classnames from "classnames";
import { Link } from "react-router-dom";
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import AvForm from "availity-reactstrap-validation/lib/AvForm";
import Dropzone from "react-dropzone";

import stateWrapper from "../../../containers/provider";

import instance from "../../../helpers/axiosly";

const SubmitProject = (props) => {

    const [activeTab, setactiveTab] = useState(1)
    const [selectedFiles, setselectedFiles] = useState([]);

    const [state, setState] = useState({
        title: "submitted title",
        description: "",
        additionMaterial: "",
        loading: false,
        userData: null
    });



    const onChange = (e) => {
        // let { name, value } = e.target;
        // setState({
        //     ...state,
        //     [name]: value,
        // });
    }



    useEffect(() => {

        let userData;
        userData = props.userStore.fetchUser(props);;
        console.log(userData)
        console.log(userData?.access_token)
        console.log(state.title)


        setState({
            userData: userData
        })

        //eslint-disable-line
    }, [])



    function handleValidSubmit(event, values) {
        try{

            // console.log(state?.userData?.user.id)
            // console.log(state?.userData?.user)
            // const form = new FormData();
              console.log();
            // form.append("text", state.title);
            // form.append("description", state.description);
            // form.append("file", selectedFiles[0]);

            // for (var key of form.entries()) {
            //     console.log(key[1])
            // }

            // const result = await instance
            //     .post(`${'https://api.electrikrecharge.com/'}/${state?.userData?.id}/submit`, form, {
            //         headers: {
            //             "Content-Type": "multipart/form-data",
            //             Authorization: `Bearer ${state?.userData?.access_token}`,
            //         },
            //     })
            //     .then((response) => {
            //         console.log(response.data);

            //     })
            //     .catch((e) => console.log(e));

            setState({
                title: "now submitted",
                description: ""
            });

            // setselectedFiles([])
        }
        catch (e){
            console.log(e)
        }
    }



    return (
        <React.Fragment>
            <div className="page-content">
                <Container fluid>
                    {/* Render Breadcrumb */}
                    <Breadcrumbs
                        title={props.t("Submit Project")}
                        breadcrumbItem={props.t("Submit Project")}
                    />
                    <AvForm
                        className="form-horizontal"
                        onValidSubmit={(e, v) => {
                            handleValidSubmit(e, v);
                        }}
                    >
                        <Row>
                            <Col lg="12">
                                <Card>
                                    <CardBody>


                                        <div id="basic-pills-wizard" className="twitter-bs-wizard">
                                            <ul className="twitter-bs-wizard-nav nav nav-justified nav-pills">
                                                <NavItem>
                                                    <NavLink
                                                        className={classnames({ active: activeTab === 1 })}
                                                        onClick={() => {
                                                            setactiveTab(1)
                                                        }}
                                                    >
                                                        <span className="step-number mr-2">01</span>
                        Submit Project
                      </NavLink>
                                                </NavItem>
                                            </ul>
                                            <TabContent
                                                activeTab={activeTab}
                                                className="twitter-bs-wizard-tab-content"
                                            >
                                                <TabPane tabId={1}>
                                                    <Row>
                                                        <Col lg="6">
                                                            <FormGroup>
                                                                <Label for="basicpill-address-input2">
                                                                    ADDITIONAL NOTE
                            </Label>
                                                                <textarea
                                                                    value={state.description}
                                                                    onChange={onChange}
                                                                    name="description"
                                                                    id="basicpill-address-input2"
                                                                    className="form-control"
                                                                    rows="11"
                                                                ></textarea>
                                                            </FormGroup>
                                                        </Col>
                                                        <Col lg="6">
                                                            <Label for="basicpill-address-input2">
                                                                ADDITIONAL MATERIAL
                            </Label>
                                                            <Dropzone
                                                                onDrop={(acceptedFiles) => {
                                                                    handleAcceptedFiles(acceptedFiles);
                                                                }}

                                                            >
                                                                {({ getRootProps, getInputProps }) => (
                                                                    <div className="dropzone">
                                                                        <div
                                                                            className="dz-message needsclick mt-2"
                                                                            {...getRootProps()}
                                                                        >
                                                                            <input {...getInputProps()} />
                                                                            <div className="mb-3">
                                                                                <i className="display-4 text-muted bx bxs-cloud-upload"></i>
                                                                            </div>
                                                                            <h5>
                                                                                Drop files here or click to upload.
                                      </h5>
                                                                        </div>
                                                                    </div>
                                                                )}
                                                            </Dropzone>
                                                            <div className="dropzone-previews mt-3" id="file-previews">
                                                                {state.loading ? (<div class="spinner-border" role="status">
                                                                    <span class="sr-only">Loading...</span>
                                                                </div>) : selectedFiles.map((f, i) => {
                                                                    return (
                                                                        <Card key={i + "-file"} className="mt-1 mb-0 shadow-none border dz-processing dz-image-preview dz-success dz-complete">
                                                                            <div className="p-2">
                                                                                <Row className="align-items-center">
                                                                                    <Col className="col-auto">
                                                                                        <img
                                                                                            data-dz-thumbnail=""
                                                                                            height="80"
                                                                                            className="avatar-sm rounded bg-light"
                                                                                            alt={f.name}
                                                                                            src={f.preview}
                                                                                        />
                                                                                    </Col>
                                                                                    <Col>
                                                                                        <Link
                                                                                            to="#"
                                                                                            className="text-muted font-weight-bold"
                                                                                        >
                                                                                            {f.name}
                                                                                        </Link>
                                                                                        <p className="mb-0">
                                                                                            <strong>{f.formattedSize}</strong>
                                                                                        </p>
                                                                                    </Col>
                                                                                </Row>
                                                                            </div>

                                                                        </Card>
                                                                    )
                                                                })}
                                                            </div>
                                                        </Col>
                                                    </Row>
                                                </TabPane>
                                            </TabContent>
                                        </div>
                                        <button
                                            type="submit"
                                            className="btn btn-warning waves-effect waves-light"
                                        >
                                            Submit
                    </button>
                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                    </AvForm>
                </Container>
            </div>
        </React.Fragment>
    );
};

export default withNamespaces()(stateWrapper(SubmitProject));
