import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
} from "reactstrap";
import { Link } from "react-router-dom";

// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";
import instance from "../../../helpers/axiosly";



import * as _ from "lodash";
import stateWrapper from "../../../containers/provider";



// Pages Components
// import LatestTranaction from "./LatestTranaction";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";

const CreateSubjectArea = (props) => {

  const [state, setState] = useState({
    name: "",
    price: "",
    loading: false
  });

  const[updateSubject, setUpdateSubject] = useState("");


  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
        try {
          const token = localStorage.getItem("token")
          const res = await instance
            .get(`${process.env.REACT_APP_DATABASEURL}/subject-area`, {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            })
            setState({...state, loading: false})
            let currentSubjects = res.data.data.rows

            let currentSubject = currentSubjects.filter((e) => e.id === Number(props.match.params.id))



          if(props.match.params.id){
            setUpdateSubject("update")
              setState({
              ...state,
              name: currentSubject.map(e => {return (e.name)}),
              price: currentSubject.map(e => {return (e.price)})
              })
          } else {
              setState({
              ...state,
              name: '',
              price: ''
            })
            }

        } catch (e) {
          console.log(e);
        }
        }
        fetchData();


    //eslint-disable-line
  }, [])


  async function handleSubmit(e) {
    e.preventDefault()
    try {
      const token = localStorage.getItem("token")
      const form = new FormData();
      console.log(state.name)
      form.append("name", state.name);
      form.append("price", state.price); //state.serviceType);
      console.log(form)
      await instance
          .post(`${process.env.REACT_APP_DATABASEURL}/subject-area`, form, {
            headers: {
              "Content-Type": "multipart/form-data",
              Authorization: `Bearer ${token}`,
            },
          }).then((response) => {
              console.log(response.data.data)})
              .catch((e) => console.log(e));


    }
     catch (e) {
      console.log(e);
    }
}

async function handleUpdate(e){
  const token = localStorage.getItem("token")
  e.preventDefault()
  try {
    let formData = new FormData();
    console.log(state.name)
    let subjectname = state.name
    let subjectprice = state.price
    let id = Number(props.match.params.id)
    formData.append("name", subjectname);
    formData.append("price", subjectprice); //state.serviceType);

    for(var key of formData.entries()){
      console.log(key[1])
    }
    await instance
        .put(`${process.env.REACT_APP_DATABASEURL}/subject-area/${id}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }).then((response) => {
            console.log(response.data.data)})
            .catch((e) => console.log(e));


  }
   catch (e) {
    console.log(e);
  }
}

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Subject Areas")}
            breadcrumbItem={props.t("Create Subject Areas")}
          />

          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                  {updateSubject === "" ? <CardTitle>CREATE SUBJECT AREAS</CardTitle> :
                    <CardTitle>UPDATE SUBJECT AREAS</CardTitle>
                  }

                  {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):(
                      <form onSubmit={handleSubmit}>
                      <Row>
                        <Col lg="6">
                          <FormGroup>
                            <Label>
                              SUBJECT AREAS
                            </Label>
                            <Input
                              value={state.name}
                              onChange={(e) =>
                                setState({ ...state, name: e.target.value })
                              }
                              type="text"
                              className="form-control"

                            />
                          </FormGroup>
                        </Col>

                        <Col lg="6">
                          <FormGroup>
                            <Label>
                              Price
                            </Label>
                            <Input
                              value={state.price}
                              onChange={(e) =>
                                setState({ ...state, price: e.target.value })
                              }
                              type="text"
                              className="form-control"

                            />
                          </FormGroup>
                        </Col>
                      </Row>
                       {updateSubject === "" ? (
                         <div className=" ">
                         <button
                           type="submit"
                           className="btn btn-primary waves-effect waves-light mx-2"
                           type="submit"
                         >
                           Post Subject
                         </button>
                         </div>
                       ) :
                       (
                        <div>
                        <button
                          className="btn btn-primary waves-effect waves-light mx-2"
                          type="submit"
                          onClick={handleUpdate}
                        >
                          Update Subject
                        </button>
                      </div>
                       )}

                    </form>
                  )}

                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>

);
};

export default withNamespaces()(stateWrapper(CreateSubjectArea));
