import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
  Alert,
} from "reactstrap";

// availity-reactstrap-validation
import { AvForm, AvField } from "availity-reactstrap-validation";

import { CountryDropdown } from "react-country-region-selector";

import stateWrapper from "../../containers/provider";

// action
import {
  registerUser,
  apiError,
  registerUserFailed,
} from "../../store/actions";

// Redux
import { connect } from "react-redux";
import { Link } from "react-router-dom";

// import images
import profileImg from "../../assets/images/profile-img.png";
import logoImg from "../../assets/images/logo.svg";

const WriterSignup = (props) => {
  const [confirmPassword, setConfirmPassword] = useState("");
  const [state, setState] = useState({
    firstname: "",
    lastname: "",
    othername:"",
    phone: null,
    email: "",
    password: "",
    dob: "",
    sex: "",
    state: "",
    address: "",
    country: "",
    qualification: "",
    role: 3,
    loading: false,
    status:"",
  });

  // handleValidSubmit
  // const handleSubmit = async (values) => {
  const handleValidSubmit = async () => {
    setState({
      loading: true,
    });
    console.log("see val",state)
    if (state.password === confirmPassword) {
      const res = await props.userStore.registerUser(state);
      const filterUserId = (resData) => {
        var str = resData;
        var res = str.replace(/\D/g, "");
        return res;
      };

      // check if the registration is successful then verify the account
      if (res.data.status === "Success") {
        const verifyId = filterUserId(res.data.data);
        console.log("check reeee", verifyId);
        props.userStore.verifyUser(verifyId, props);
        setState({
          loading: false,
          status: "Success",
        });
      } else {
        setState({
          loading: false,
        });
        console.log("no response");
      }
      setState({
        loading: false,
      });
      console.log(state);
    }
  };

  useEffect(() => {
    props.registerUserFailed("");
  });

  return (
    <React.Fragment>
      <div className="home-btn d-none d-sm-block">
        <Link to="/" className="text-dark">
          <i className="fas fa-home h2"></i>
        </Link>
      </div>
      <div className="account-pages my-5 pt-sm-5">
        <Container>
          <Row className="justify-content-center">
            <Col md={12} lg={24} xl={24}>
              <Card className="overflow-hidden">
                <div className="bg-soft-primary">
                  <Row>
                    <Col className="col-20">
                      <div className="text-primary p-4">
                        <h3 className="text-primary">Become a writer</h3>
                        <p>Create an account to get started!</p>
                      </div>
                    </Col>
                    <Col className="col-5 align-self-end">
                      {/* <img src={profileImg} alt="" style={{height:"200px"}} className="img-fluid" /> */}
                    </Col>
                    {
                      state.status === "Success" ? (

                        <div className="alert alert-info alert-dismissible fade show" role="alert">
                        Success! click on signin to signin to your account
                        </div>
                      )
                      : ('')
                    }
                  </Row>
                </div>
                <CardBody className="pt-0">
                  <div>
                    <Link to="/">
                      <div className="avatar-md profile-user-wid mb-4">
                        <span className="avatar-title rounded-circle bg-light">
                          <img
                            src={logoImg}
                            alt=""
                            className="rounded-circle"
                            height="34"
                          />
                        </span>
                      </div>
                    </Link>
                  </div>
                  <div className="p-2">
                    <AvForm
                      className="form-horizontal"
                      onValidSubmit={(e, v) => {
                        handleValidSubmit(e, v);
                      }}
                    >
                      {props.user && props.user ? (
                        <Alert color="success">
                          Register User Successfully
                        </Alert>
                      ) : null}
                      {props.registrationError && props.registrationError ? (
                        <Alert color="danger">{props.registrationError}</Alert>
                      ) : null}
                        <Row>
                         <Col>
                        <FormGroup>
                          <Label>First Name</Label>
                          <Input
                            value={state.firstname}
                            onChange={(e) =>
                              setState({ ...state, firstname: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="Enter First Name"
                            required
                          />
                        </FormGroup>
                         </Col>

                         <Col>
                         <FormGroup>
                          <Label>Last Name</Label>
                          <Input
                            value={state.lastname}
                            onChange={(e) =>
                              setState({ ...state, lastname: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="Enter Last Name"
                            required
                          />
                        </FormGroup>
                         </Col>
                        </Row>

                        <Row>
                         <Col>
                        <FormGroup>
                          <Label>Other Name</Label>
                          <Input
                            value={state.othername}
                            onChange={(e) =>
                              setState({ ...state, othername: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="Enter Other Name (optional)"
                          />
                        </FormGroup>
                         </Col>

                         <Col>
                         <FormGroup>
                          <Label>Email</Label>
                          <Input
                            value={state.email}
                            onChange={(e) =>
                              setState({ ...state, email: e.target.value })
                            }
                            placeholder="Enter Email"
                            type="text"
                            className="form-control"
                          />
                        </FormGroup>
                         </Col>
                        </Row>

                        <Row>
                         <Col>
                        <FormGroup>
                          <Label>Address </Label>
                          <Input
                            value={state.address}
                            onChange={(e) =>
                              setState({ ...state, address: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="Enter address"
                          />
                        </FormGroup>
                         </Col>

                        <Col>
                          <Row>
                          <Col>
                         <FormGroup>
                          <Label>DOB</Label>
                          <Input
                            value={state.dob}
                            onChange={(e) =>
                              setState({ ...state, dob: e.target.value })
                            }
                            placeholder="select DOB"
                            type="date"
                            className="form-control"
                          />

                        </FormGroup>
                         </Col>

                         <Col>
                         <FormGroup>
                          <Label>Gender</Label>
                          {/* <select
                                  className="custom-select"
                                  // value={state.serviceType}
                                  name="gender"
                                  // onChange={onChange}
                                >
                                  <option selected disabled hidden>gender </option>
                                      <option value="M" id={0}>
                                        M
                                      </option>
                                      <option value="F" id={1}>
                                        F
                                      </option>
                                      <option value="others" id={2}>
                                        others
                                      </option>
                                </select> */}
                                <select
                                  className="custom-select"
                                  value={state.sex}
                                  name="gender"
                                    onChange={(e) =>
                              setState({ ...state, sex: e.target.value })
                            }
                                >
                                  <option defaultValue>Select DOB </option>
                                  <option value="Male" id={0}>M</option>
                                  <option value="Female" id={1}>F</option>
                                  <option value="others" id={2}>
                                        others
                                      </option>
                                </select>
                        </FormGroup>
                         </Col>
                          </Row>
                        </Col>
                        </Row>

                        {/* row 4 */}
                        <Row>
                        <Col>
                         <FormGroup>
                          <Label>Phone No</Label>
                          <Input
                            value={state.phone}
                            onChange={(e) =>
                              setState({ ...state, phone: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="Enter Phone"
                            required
                          />
                        </FormGroup>
                         </Col>

                        <Col>
                        <Row>
                        <Col>
                         <FormGroup>
                          <Label>Country</Label>
                          <CountryDropdown
                            className="custom-select"
                            value={state.country}
                            onChange={(value) =>
                              setState({ ...state, country: value })
                            }
                            showDefaultOption={true}
                            defaultOptionLabel={"Select Country"}
                          ></CountryDropdown>
                        </FormGroup>
                         </Col>

                         <Col>
                         <FormGroup>
                          <Label>State</Label>
                          <Input
                            value={state.state}
                            onChange={(e) =>
                              setState({ ...state, state: e.target.value })
                            }
                            placeholder="state"
                            type="text"
                            className="form-control"
                          />
                        </FormGroup>
                         </Col>
                        </Row>
                        </Col>
                        </Row>

                        {/* row 5 */}
                        <Row>
                         <Col>
                        <FormGroup>
                          <Label>School</Label>
                          <Input
                            value={state.school}
                            onChange={(e) =>
                              setState({ ...state, school: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="school"
                            required
                          />
                        </FormGroup>
                         </Col>

                         <Col>
                         <FormGroup>
                          <Label>Degree(s)</Label>
                          <Input
                            value={state.qualification}
                            onChange={(e) =>
                              setState({ ...state, qualification: e.target.value })
                            }
                            type="text"
                            className="form-control"
                            placeholder="degree"
                            required
                          />
                        </FormGroup>
                         </Col>
                        </Row>

                        {/* row 6 */}
                        <Row>
                         <Col>
                         <FormGroup>
                          <Label>Password</Label>
                          <Input
                            value={state.password}
                            onChange={(e) =>
                              setState({ ...state, password: e.target.value })
                            }
                            type="password"
                            className="form-control"
                          />
                        </FormGroup>
                         </Col>

                         <Col>
                         <FormGroup>
                          <Label>Confirm New Password</Label>
                          <Input
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            type="password"
                            className="form-control"
                          />
                        </FormGroup>
                         </Col>
                        </Row>


                      <div className="mt-4">
                        {state.loading ? (
                          <div
                            className="spinner-grow text-primary m-1"
                            role="status"
                          >
                            {/* <span className="sr-only">Loading...</span> */}
                          </div>
                        ) : (
                          <button
                            className="btn btn-primary btn-block waves-effect waves-light"
                            type="submit"
                          >
                            Register
                          </button>
                        )}
                      </div>

                      <div className="mt-4 text-center">
                        <p className="mb-0">
                          By registering you agree to the AcadWritings{" "}
                          <Link to="#" className="text-primary">
                            Terms of Use
                          </Link>
                        </p>
                      </div>
                    </AvForm>
                  </div>
                </CardBody>
              </Card>
              <div className="mt-5 text-center">
                <p>
                  Already have an account ?{" "}
                  <Link to="/login" className="font-weight-medium text-primary">
                    {" "}
                    Login
                  </Link>{" "}
                </p>
                {/* <p>
                    © {new Date().getFullYear()} AcadWritings. Crafted with{" "}
                    <i className="mdi mdi-heart text-danger"></i> by Themesbrand
                  </p> */}
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};

const mapStatetoProps = (state) => {
  const { user, registrationError, loading } = state.Account;
  return { user, registrationError, loading };
};

export default connect(mapStatetoProps, {
  registerUser,
  apiError,
  registerUserFailed,
})(stateWrapper(WriterSignup));
