import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
} from "reactstrap";
import { Link } from "react-router-dom";

// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";
import instance from "../../../helpers/axiosly";



import * as _ from "lodash";
import stateWrapper from "../../../containers/provider";



// Pages Components
// import LatestTranaction from "./LatestTranaction";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";

const CreateSpacing = (props) => {

  const [state, setState] = useState({
    name: "",
    loading: false
  });

  const[updateSpacing, setUpdateSpacing] = useState("");




  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
        try {
          const token = localStorage.getItem("token")
          const res = await instance
            .get(`${process.env.REACT_APP_DATABASEURL}/spacing`, {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            })
            setState({...state, loading: false})
            let currentSpacings = res.data.data

            let currentSpacing = currentSpacings.filter((e) => e.id === Number(props.match.params.id))


          if(props.match.params.id){
            setUpdateSpacing("update")
              setState({
              ...state,
              name: currentSpacing.map(e => {return (e.name)})
              })
          } else {
              setState({
              ...state,
              name: ''
            })
            }

        } catch (e) {
          console.log(e);
        }
        }
        fetchData();


    //eslint-disable-line
  }, [])




  async function handleSubmit(e) {
    const token = localStorage.getItem("token")
    e.preventDefault()
    try {
      let form = new FormData();

      form.append("name", state.name); //state.serviceType);

      for(var key of form.entries()){
        console.log(key[1])
      }
      await instance
          .post(`${process.env.REACT_APP_DATABASEURL}/spacing`, form, {
            headers: {
              "Content-Type": "multipart/form-data",
              Authorization: `Bearer ${token}`,
            },
          }).then((response) => {
              console.log(response.data.data)})
              .catch((e) => console.log(e));


    }
     catch (e) {
      console.log(e);
    }
}

  async function handleUpdate(e){
  const token = localStorage.getItem("token")
  e.preventDefault()
  try {
    let formData = new FormData();
    let servicename = state.name
    let id = Number(props.match.params.id)
    formData.append("name", servicename);

    for(var key of formData.entries()){
      console.log(key[1])
    }
    await instance
        .put(`${process.env.REACT_APP_DATABASEURL}/spacing/${id}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }).then((response) => {
            console.log(response.data.data)})
            .catch((e) => console.log(e));


  }
   catch (e) {
    console.log(e);
  }
}

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Spacing")}
            breadcrumbItem={props.t("Create Spacing")}
          />

          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                {updateSpacing === "" ? <CardTitle>CREATE SPACING</CardTitle> :
                    <CardTitle>UPDATE SPACING</CardTitle>
                  }
                  {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):(
                    <form onSubmit={handleSubmit}>
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <Label>
                            Spacing
                          </Label>
                          <Input
                            value={state.name}
                            onChange={(e) =>
                              setState({ ...state, name: e.target.value })
                            }
                            type="text"
                            className="form-control"

                          />
                        </FormGroup>
                      </Col>




                    </Row>

                    {updateSpacing === "" ? (
                       <div className=" ">
                       <button
                         type="submit"
                         className="btn btn-primary waves-effect waves-light my-2"
                         type="submit"
                       >
                         Post Spacing
                       </button>
                       </div>
                     ) : (
                      <div>
                      <button
                        className="btn btn-primary waves-effect waves-light mx-2"
                        type="submit"
                        onClick={handleUpdate}
                      >
                        Update Spacing
                      </button>
                    </div>
                     )}


                  </form>
                  )}

                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>

);
};

export default withNamespaces()(stateWrapper(CreateSpacing));
