import React, { useEffect, useState } from "react";
import {  Container, Row, Col, Card, CardBody, Label, CardTitle, Input, Form, FormGroup, CustomInput, ListGroup,ListGroupItem, ButtonGroup, Button, Jumbotron, Progress } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from 'react-moment'
import AvForm from "availity-reactstrap-validation/lib/AvForm";
import Questionform from './question'
import Startquestion from './Startquestion'
import Result from './results'


const Questions = (props) => {

  const Question = [
    {
      "question" : "What is capital of Croatia",
      "options": ["Split", "osijek", "Zagreb"],
      "answer": "Zagreb",
      "score": 100
    },
    {
      "question" : "What is capital of Slovenia",
      "options": ["Split", "Ljubljana", "Zagreb"],
      "answer": "Ljubljana",
      "score": 200
    },
    {
      "question" : "What is capital of Austria",
      "options": ["Split", "Vienna", "Zagreb"],
      "answer": "Vienna",
      "score": 300
    }
  ]

  const [step, setStep] = useState(1);
  const [activeQuestion, setActiveQuestion] = useState(0);
  const [answers, setAnswers] = useState([])
  const [showPrev, setShowPrev] = useState(0)
  const[userId, setUserId] = useState(0)
  const [score, setScore] = useState(0)

  console.log(answers)
  let id = Number(props.match.params.id)
  let rediect = props.history

  useEffect(() => {
    let userData = props.userStore.fetchUser(props);
    setUserId(userData.id)
    //eslint-disable-line
  }, [])


  /*const[questions, setQuestions] = useState([]);
  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {

    async function fetchData(){
      setState({...state, loading: true})
    try {
      const token = localStorage.getItem("token")
      let id = props.match.params.id
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}exam/${id}/question`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setQuestions(res.data.data)

        console.log(res.data.data)


    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    //eslint-disable-line
  }, [])

  const qstOpt = questions.map((a, i) => {return a.options})
  const qst = JSON.stringify(qstOpt)
  console.log(qst)*/


  const onQuestionHandler = () => {
    setStep(2)
  }

  return (
    <React.Fragment>
        <div className="page-content">
        <Container fluid>
        <Row>
        <Col xl='2'></Col>
        <Col xl='8'>
        {step === 1 && <Startquestion onQuestionStart={onQuestionHandler}/>}
        {step === 2 && <Questionform
          data={Question[activeQuestion] }
          onAnswerUpdate = {setAnswers}
          numberOfQuestion= {Question.length}
          activeQuestion = {activeQuestion}
          onSetActiveQuestion = {setActiveQuestion}
          setShowPrev = {setShowPrev}
          onSetStep= {setStep}
          setScore={setScore}
          score={score}
        />}
        {step === 3 && <Result
        results={answers}
        data={Question}
        questionId={id}
        userId={userId}
        score={score}
        rediect={rediect}
        />}
        </Col>
        <Col xl='2'></Col>
        </Row>

        </Container>
    </div>

    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Questions));
