import React, {useEffect, useState} from 'react';
import { CardBody, Card, Button } from 'reactstrap';
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import instance from "../../../helpers/axiosly";

const Result = ({results, data, questionId, userId, score, rediect}) => {

    const[state, setState] = useState({
        loading: false,
        alert: ''
      })
    const[correctAnswers, setCorrectAnswers] = useState(0)
    const [scores, setScores] = useState([])
    //const [score, setScore] = useState(0)

    //const sco = results.map(result => {return result.s})
    //setScore(sco)



   /* useEffect(() => {


        let correct = 0;

        results.forEach((result, index) => {

            if(result.a === data[index].answer){
                console.log(result.s)
               // setScores({score: score + result.s})
            } else if(result.a !== data[index].answer) {
                setScores(0)
            }
        })
        setCorrectAnswers(correct)



        //eslint-disable-next-line
    }, [])*/

     //.reduce((prev, next)=> prev + next);


    // const sum = scores.reduce((n, {score}) => n + score, 0)
     console.log(score)

     const submitHandler = () => {
        setState({...state, loading: true});
        async function fetchData(){
            console.log(questionId)
            console.log(userId)

            try {
              const token = localStorage.getItem("token")
              const res = await instance
                .post(`${process.env.REACT_APP_DATABASEURL}/exam/${questionId}/user/${userId}`, {
                    score: score
                },{
                  headers: {
                    Authorization: `Bearer ${token}`,
                  },
                })
                console.log(res.data)
                if(res.data.status == 'Success') {
                    setState({...state, loading: false, alert: true})
                }

            } catch (e) {
              console.log(e);
            }
            }
            fetchData();

            setTimeout(function(){
              rediect.push("/wexam");
              }, 4000)
     }

    return(
        <Card body className="text-center">
          <div>
          {state.alert ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Results submitted
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
                ) : ""}
                <h3 className="card-title mb-4 text-info">Your Score: </h3>
                <h2>{score}</h2>
                {state.loading ? (
                    <div
                    className="spinner-grow text-primary m-1"
                    role="status"
                 >
                    {/* <span className="sr-only">Loading...</span> */}
                </div>
                ):(
                    <Button color="primary" className="btn btn-primary waves-effect waves-light mx-4" onClick={submitHandler}>
                    Submit Results
                </Button>
                    )}
          </div>


        </Card>
    )
}

export default withNamespaces()(stateWrapper(Result));
