import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from "react-moment";



const ExamList = (props) => {

  const[exams, setExams] = useState([]);

  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {
    setState({...state, loading: true})

    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/exam`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setExams(res.data.data)


    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    //eslint-disable-line
  }, [])



  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <CardTitle className="mb-4">ALL EXAMS</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>) : (
         <div className="table-responsive">
         <table className="table table-centered table-nowrap mb-0">
           <thead className="thead-light">
             <tr>
               <th style={{ width: "20px" }}>
                 <div className="custom-control custom-checkbox">
                   <input
                     type="checkbox"
                     className="custom-control-input"
                     id="customCheck1"
                   />
                   <label
                     className="custom-control-label"
                     htmlFor="customCheck1"
                   >
                     &nbsp;
                   </label>
                 </div>
               </th>
               <th>Exams</th>
               <th>Created</th>
               <th>Updated</th>
               <th>{" "}</th>
             </tr>
           </thead>
           <tbody>
             {exams && exams.map((exam, key) => (
               <tr key={"_tr_" + key}>
                 <td>
                   <div className="custom-control custom-checkbox">
                     <input
                       type="checkbox"
                       className="custom-control-input"
                       id={exam.id}
                     />
                     <label
                       className="custom-control-label"
                       htmlFor={exam.id}
                     >
                       &nbsp;
                     </label>
                   </div>
                 </td>
                 <td>
                   <Link to="#" className="text-body font-weight-bold">
                     {" "}
                     {exam.name}{" "}
                   </Link>{" "}
                 </td>
                 <td><Moment format="YYYY/MM/DD">{exam.createdAt}</Moment></td>
                 <td><Moment format="YYYY/MM/DD">{exam.updatedAt}</Moment></td>
                 <td>

                    <Link to={"/wquestion/" + exam.id}>
                      <Button
                      color="primary"
                      className="btn btn-primary waves-effect waves-light mx-2"
                          >
                      {" "}
                      Questions
                      </Button>
                      </Link>

                  </td>
               </tr>
             ))}
           </tbody>
         </table>
       </div>
      )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};



export default (withNamespaces()(stateWrapper(ExamList)));
