import {grantPermission} from '../../GrantPermission'
import {ROLES} from '../../App.constants';
import stateWrapper from '../../containers/provider';

function Writermiddleware({
		component: Component, layout: Layout, roles, ...rest
	  }) {
		useEffect(() => {
			(async () => {
			//   const userData = rest?.userStore?.fetchUser();
			//   const bearertoken = userData?.access_token;
			//   console.log(userData, bearertoken);
			//   const result = rest?.orderStore?.fetchOrders(userData, bearertoken);
			//   console.log(result);
			 const writerExamStatus = rest?.masterStore?.find
			})()

		}, [])
		return (
		  <>
			{ grantPermission([ROLES.WRITER]) && (
			<Route
			  {...rest}
			//   findWriterExam
			  render={(props) => (
				<Layout>
				  <Component {...props} />
				</Layout>
			  )}
			/>
			)}
			{
			  !grantPermission([ROLES.WRITER]) && (
				<Route
				{...rest}
				render={(props) => (
					<Redirect  to={{ pathname: "/login", state: { from: props.location } }} />
				)}

				/>

			  )
			}
		  </>
		);
	  }

export default withRouter(stateWrapper(Writermiddleware));
