import React, { useState, useEffect  } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Media,
  Table,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  FormGroup,
  Label,
  Input,
} from "reactstrap";
import classnames from "classnames";
import { Link } from "react-router-dom";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import AvForm from "availity-reactstrap-validation/lib/AvForm";
import Dropzone from "react-dropzone";

import stateWrapper from "../../containers/provider";

import instance from "../../helpers/axiosly";

const Annoucement = (props) => {

  const[activeTab, setactiveTab] = useState(1)
  const [selectedFiles, setselectedFiles] = useState([]);

  const [state, setState] = useState({
    title: "",
    description: "",
    additionMaterial: "",
    loading: false,
    droploading: false
  })

  const[updateAnnoucement, setUpdateAnnoucement] = useState("");

  useEffect(() => {



    //eslint-disable-line
  }, [])

  let libraries = props.masterStore.state.sessionData.AnnoucementList;

  let currentAnnoucement = libraries && libraries.filter((e) => e.id === Number(props.match.params.id))


  const onChange = (e) => {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  }


  async function handleValidSubmit(e) {
    setState({...state,
      loading: true,
    });
    let  userData;
    userData = props.userStore.fetchUser(props);
    e.preventDefault()
    props.masterStore.addAnnoucement(state, userData.access_token, selectedFiles[0])

    setTimeout(function(){
      setState({...state,
        title: "",
        description: "",
        loading: false
      })
    }, 2000)

    setselectedFiles([])

    setTimeout(function(){
     props.history.push({pathname:"/Annoucement", state:{status: 'created'}});
    }, 2000)

  }

  const handleUpdate = (e) => {
    setState({...state,
      loading: true,
    });
    let  userData;
    userData = props.userStore.fetchUser(props);



    e.preventDefault()
    let id = Number(props.match.params.id)

    props.masterStore.updateAnnoucement(state, userData.access_token, selectedFiles[0], id)

    setTimeout(function(){
      setState({...state,
        title: "",
        description: "",
        loading: false
      })
    }, 2000)
    setselectedFiles([])

    setTimeout(function(){
     props.history.push({pathname:"/Annoucement", state:{status: 'updated'}});
    }, 2000)

  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Annoucement")}
            breadcrumbItem={props.t("Annoucement")}
          />
           <AvForm
           className="form-horizontal"
           onValidSubmit={(e, v) => {
             handleValidSubmit(e, v);
           }}
           >
            <Row>
              <Col lg="12">
                <Card>
                  <CardBody>


                  <div id="basic-pills-wizard" className="twitter-bs-wizard">
                  <ul className="twitter-bs-wizard-nav nav nav-justified nav-pills">
                    <NavItem>
                      <NavLink
                        className={classnames({active: activeTab === 1})}
                        onClick={() => {
                          setactiveTab(1)
                        }}
                      >
                        <span className="step-number mr-2">01</span>
                        Annoucement Details
                      </NavLink>
                    </NavItem>
                  </ul>
                  <TabContent
                    activeTab={activeTab}
                    className="twitter-bs-wizard-tab-content"
                  >
                    <TabPane tabId={1}>
                     <Row>
                      <Col lg="12">
                        <FormGroup>
                          <Label for="basicpill-firstname-input14">
                            Annoucement Title
                          </Label>
                          <Input
                             value={state.title}
                             onChange={onChange}
                             name="title"
                            type="text"
                            id="basicpill-firstname-input14"
                            className="form-control"
                          />
                        </FormGroup>
                      </Col>
                      </Row>
                      <Row>
                        <Col lg="6">
                        <FormGroup>
                            <Label for="basicpill-address-input2">
                              DESCRIPTION
                            </Label>
                            <textarea
                               value={state.description}
                               onChange={onChange}
                              name="description"
                              id="basicpill-address-input2"
                              className="form-control"
                              rows="11"
                            ></textarea>
                          </FormGroup>
                        </Col>
                        <Col lg="6">
                        <Label for="basicpill-address-input2">
                              ADDITION MATERIAL
                            </Label>
                            <Dropzone
                              onDrop={(acceptedFiles) => {
                                handleAcceptedFiles(acceptedFiles);
                              }}
                            >
                              {({getRootProps, getInputProps}) => (
                                  <div className="dropzone">
                                  <div className="dz-message needclick mt-2" {...getRootProps()}>
                                    <input {...getInputProps()}/>
                                    <div className="mb-3">
                                    <i className="display-4 text-muted bx bxs-cloud-upload"></i>
                                    </div>
                                    <h5>
                                        Drop files here or click to upload.
                                      </h5>
                                  </div>
                                </div>
                              )}

                            </Dropzone>
                            <div className="dropzone-previews mt-3" id="file-previews">
                                {state.droploading ? (<div class="spinner-border" role="status">
                                    <span class="sr-only">Loading...</span>
                                    </div>) :  selectedFiles.map((f, i) => {
                                  return (
                                    <Card key={i + "-file"} className="mt-1 mb-0 shadow-none border dz-processing dz-image-preview dz-success dz-complete">
                                       <div className="p-2">
                                        <Row className="align-items-center">
                                          <Col className="col-auto">
                                            <img
                                              data-dz-thumbnail=""
                                              height="80"
                                              className="avatar-sm rounded bg-light"
                                              alt={f.name}
                                              src={f.preview}
                                            />
                                          </Col>
                                          <Col>
                                            <Link
                                              to="#"
                                              className="text-muted font-weight-bold"
                                            >
                                              {f.name}
                                            </Link>
                                            <p className="mb-0">
                                              <strong>{f.formattedSize}</strong>
                                            </p>
                                          </Col>
                                        </Row>
                                      </div>

                                    </Card>
                                  )
                                })}
                            </div>
                        </Col>
                      </Row>
                    </TabPane>
                  </TabContent>
                  </div>
                  {updateAnnoucement  === "" ?
                    (
                      <div>
                        {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                         type="submit"
                          className="btn btn-warning waves-effect waves-light"
                        >
                          Submit
                        </button>
                         )}

                      </div>

                    ): (
                      <div>
                        {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                          type="submit"
                          className="btn btn-warning waves-effect waves-light"
                          onClick={handleUpdate}
                        >
                          Update
                        </button>
                         )}

                    </div>
                    )
                  }

                  </CardBody>
                </Card>
              </Col>
            </Row>
          </AvForm>
          </Container>
          </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Annoucement));
