import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Media,
  Table,
} from "reactstrap";

import {Link} from 'react-router-dom'
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import instance from "../../../helpers/axiosly";

import fileDownload from 'js-file-download'

const Library = (props) => {

  const [state, setState] = useState({
    library: [],
    loading: false
  });


  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/library`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        console.log(res.data.data.rows)
        setState({
          ...state,
          library: res.data.data.rows,
          loading: false
        });
    } catch (e) {
      console.log(e);
    }
    }
    fetchData();

    //eslint-disable-line
  }, [])

  function handleSubmit (file) {
    //fileDownload(file)
  }


  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Library")}
          />
          <Card>
            <CardBody>
              <CardTitle>LIBRARY</CardTitle>
              {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>) :  (
                    <div className="table-responsive">
                    <table className="table table-centered table-nowrap mb-0">
                      <thead className="thead-light">
                        <tr>
                          <th style={{ width: "20px" }}>
                            <div className="custom-control custom-checkbox">
                              <input
                                type="checkbox"
                                className="custom-control-input"
                                id="customCheck1"
                              />
                              <label
                                className="custom-control-label"
                                htmlFor="customCheck1"
                              >
                                &nbsp;
                              </label>
                            </div>
                          </th>
                          <th>Title</th>
                          <th>Description</th>
                          <th>Download File</th>
                        </tr>
                      </thead>
                      <tbody>
                        {state.library.map((list, key) => (
                          <tr key={"_tr_" + key}>
                            <td>
                              <div className="custom-control custom-checkbox">
                                <input
                                  type="checkbox"
                                  className="custom-control-input"
                                  id={list.id}
                                />
                                {/* ({let total =  list.amount + list.balance}) */}

                                <label
                                  className="custom-control-label"
                                  htmlFor={list.id}
                                >
                                  &nbsp;
                                </label>
                              </div>
                            </td>
                            <td>{list.title}</td>
                            <td>{list.description}</td>
                            <td>
                              <Link
                                className="btn btn-primary waves-effect waves-light"
                                type="submit"
                                onClick={handleSubmit(list.url)}
                              >
                                Download
                              </Link>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                  )

              }
            </CardBody>
          </Card>
          </Container>
          </div>
    </React.Fragment>
  );
};

export default withNamespaces()(Library);
