import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
} from "reactstrap";
import { Link } from "react-router-dom";

// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";
import instance from "../../../helpers/axiosly";



import * as _ from "lodash";
import stateWrapper from "../../../containers/provider";



// Pages Components
// import LatestTranaction from "./LatestTranaction";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";

const CreatePaperFormat = (props) => {

  const [state, setState] = useState({
    name: "",
    loading: false
  });

  const[updatePaperFormat, setUpdatePaperFormat] = useState("");




  useEffect(() => {
    let paperFormat, userData;
    userData = props.userStore.fetchUser(props);

    paperFormat = props.masterStore.fetchPaperFormat(
      userData.access_token
    );


    if(props.match.params.id){
      setUpdatePaperFormat("update")
        setState({
        ...state,
        name: currentPaperFormat.map(e => {return (e.name)})
        })
    } else {
        setState({
        ...state,
        name: ''
      })
      }


    //eslint-disable-line
  }, [])

  let paperFormats = props.masterStore.state.sessionData.paperFormatList;

  let currentPaperFormat = paperFormats && paperFormats.filter((e) => e.id === Number(props.match.params.id))



  async function handleSubmit(e) {
    setState({...state,
      loading: true,
    });
    let  userData;
    userData = props.userStore.fetchUser(props);

    e.preventDefault()
    props.masterStore.addPaperFormat(state, userData.access_token)

    setTimeout(function(){
      setState({...state,
        name: "",
        loading: false
      })
    }, 2000)

    setTimeout(function(){
      props.history.push({pathname:"/paperformat", state:{status: 'created'}});
    }, 2000)

}

  async function handleUpdate(e){
    setState({...state,
      loading: true,
    });
    let  userData;
    userData = props.userStore.fetchUser(props);

   e.preventDefault();

   let id = Number(props.match.params.id)

   props.masterStore.updatePaperFormat(state, userData.access_token, id)

   setTimeout(function(){
     setState({...state,
       name: "",
       loading: false
     })
   }, 2000)

   setTimeout(function(){
     props.history.push({pathname:"/paperformat", state:{status: 'updated'}});
   }, 2000)
}

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Spacing")}
            breadcrumbItem={props.t("Create Spacing")}
          />

          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                {updatePaperFormat === "" ? <CardTitle>CREATE PAPER-FORMAT</CardTitle> :
                    <CardTitle>UPDATE PAPER-FORMAT</CardTitle>
                  }
                    <form onSubmit={handleSubmit}>
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <Label>
                            Paper-Format
                          </Label>
                          <Input
                            value={state.name}
                            onChange={(e) =>
                              setState({ ...state, name: e.target.value })
                            }
                            type="text"
                            className="form-control"

                          />
                        </FormGroup>
                      </Col>




                    </Row>

                    {updatePaperFormat === "" ? (
                       <div className=" ">
                         {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                          type="submit"
                          className="btn btn-primary waves-effect waves-light my-2"
                          type="submit"
                        >
                          Post Paper-format
                        </button>
                         )}

                       </div>
                     ) : (
                      <div>
                        {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                        className="btn btn-primary waves-effect waves-light mx-2"
                        type="submit"
                        onClick={handleUpdate}
                        >
                        Update Paper-format
                        </button>
                         )}

                    </div>
                     )}


                  </form>


                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>

);
};

export default withNamespaces()(stateWrapper(CreatePaperFormat));
