import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { Link,  useLocation, useHistory } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";


const LibraryList = (props) => {

  //const[librarys, setLibrary] = useState([]);
  const[users, setUsers] = useState([])
  const[state, setState] = useState({
    loading: false,
    alert: ''
  })
  const [modal, setmodal] = useState(false);

  const location = useLocation();
  const history = useHistory();

  useEffect(() => {
    setState({...state, loading: true})

    let userData;
    userData = props.userStore.fetchUser(props);

    async function fetchData(){

        const res = await props.masterStore.fetchLibrary(
            userData.access_token
          );
          if(res.data.status == 'Success'){
            setTimeout(function(){
              setState({...state, loading: false})
            }, 2000)
          }
    }
    fetchData();

    if(location.state && location.state.status) {
        setState({...state, alert: location.state})

        //delete location state
         history.replace({
           pathname: location.pathname, state: {}
         })
       }
    //eslint-disable-line
  }, [])

  const handleDelete = async (id) =>{

    const res = await props.masterStore.deleteLibrary(id);

    if(res.data.status == 'Success'){
        setState({...state, alert: 'deleted'})
        setTimeout(function(){
         setState({...state, alert: ''})
       }, 2000)
       }
       window.location.reload()
 }

 let librarys = props.masterStore.state.sessionData.libraryList;


 const[openModalLiblists , setopenModalLiblist] = useState([])
 const openModal = (id) => {
    setmodal(!modal);

    const openModalLib = librarys.filter((e) => e.id === id)
    setopenModalLiblist(openModalLib)
 }

  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <CardTitle className="mb-4">ALL LIBRARY CONTENT</CardTitle>
          {state.alert.status == "created" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> New Library Content created
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
           {state.alert.status == "updated" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Library Content updated
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
          {state.alert == "deleted" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Library Content deleted
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
         {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):(
                    <div className="table-responsive">
                    <table className="table table-centered table-nowrap mb-0">
                      <thead className="thead-light">
                        <tr>
                          <th style={{ width: "20px" }}>
                            <div className="custom-control custom-checkbox">
                              <input
                                type="checkbox"
                                className="custom-control-input"
                                id="customCheck1"
                              />
                              <label
                                className="custom-control-label"
                                htmlFor="customCheck1"
                              >
                                &nbsp;
                              </label>
                            </div>
                          </th>
                          <th>Library Topic</th>
                          <th>Description</th>
                          <th>File</th>
                          <th>{" "}</th>
                        </tr>
                      </thead>
                      <tbody>
                        {librarys && librarys.map((library, key) => (
                          <tr key={"_tr_" + key}>
                            <td>
                              <div className="custom-control custom-checkbox">
                                <input
                                  type="checkbox"
                                  className="custom-control-input"
                                  id={library.id}
                                />
                                <label
                                  className="custom-control-label"
                                  htmlFor={library.id}
                                >
                                  &nbsp;
                                </label>
                              </div>
                            </td>
                            <td>
                              <Link to="#" className="text-body font-weight-bold">
                                {" "}
                                {library.title}{" "}
                              </Link>{" "}
                            </td>
                            <td>{library.description}</td>
                            <td>{library.url}</td>
                            <td>



                              <Link to={"/createlibrary/" + library.id}>
                                <Button size="sm"
                                color="primary"
                                className="btn btn-primary waves-effect waves-light mx-2"
                                    >
                                {" "}
                                <i className="bx bx-edit"></i>
                                </Button>
                              </Link>

                              <Button
                                color="primary" size="sm"
                                className="btn btn-primary waves-effect waves-light mx-2"
                                onClick={() => handleDelete(library.id)}
                                    >
                                {" "}
                                <i className="bx bx-trash"></i>
                                </Button>
                                <Button type="button" color="primary" size="sm" className="btn-rounded waves-effect waves-light"
                                onClick={() => openModal(library.id) }
                                    >
                                {" "}
                                view library
                                </Button>
                            </td>

                          </tr>

                        ))}
                      </tbody>
                    </table>
                    <Modal isOpen={modal} role="dialog" autoFocus={true} centered={true} className="exampleModal" tabindex="-1" toggle={() => { setmodal(!modal); }}>
                           <div className="modal-content">
                           <ModalHeader toggle={() => { setmodal(!modal); }}>
                            Library Details
                            </ModalHeader >
                            <ModalBody>
                            <div className="table-responsive">
                            {openModalLiblists.map((openModalLiblist, key) => (
                                 <table className="table table-centered table-nowrap mb-0">
                                 <thead className="thead-light">
                                    <tr>
                                     <th>Library Topic</th>
                                    <td>
                                    <Link to="#" className="text-body font-weight-bold">
                                    {" "}
                                        {openModalLiblist.title}{" "}
                                </Link>{" "}
                                    </td>

                                    </tr>
                                    <tr>
                                    <th>Description</th>
                                <td>{openModalLiblist.description}</td>
                                </tr>
                                <tr>
                                    <th>File</th>
                                <td>{openModalLiblist.url}</td>
                                </tr>

                            </thead>
                            <tbody>
                         </tbody>
                            </table>
                            ))}

                        </div>
                            </ModalBody>
                            <ModalFooter>
                            <Button type="button" color="secondary" onClick={() => { setmodal(!modal); }}>Close</Button>
                        </ModalFooter>
                            </div>
                            </Modal>
                  </div>
                  )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(LibraryList));
