import React, { useState, useEffect  } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Media,
  Table,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  FormGroup,
  Label,
  Input,
} from "reactstrap";
import classnames from "classnames";
import { Link } from "react-router-dom";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import AvForm from "availity-reactstrap-validation/lib/AvForm";
import Dropzone from "react-dropzone";

import stateWrapper from "../../containers/provider";

import instance from "../../helpers/axiosly";

const Annoucement = (props) => {

  const[activeTab, setactiveTab] = useState(1)
  const [selectedFiles, setselectedFiles] = useState([]);

  const [state, setState] = useState({
    title: "",
    description: "",
    additionMaterial: "",
    loading: false,
    droploading: false
  })

  const[updateAnnoucement, setUpdateAnnoucement] = useState("");

  useEffect(() => {

    // let AnnoucementData, userData;
    // userData = props.userStore.fetchUser(props);

    // AnnoucementData = props.masterStore.fetchAnnoucement(
    //   userData.access_token
    // );

    // if(props.match.params.id){
    //   setUpdateAnnoucement("update")
    //     setState({
    //     ...state,
    //     title: currentAnnoucement.map(e => {return (e.title)}),
    //     description: currentAnnoucement.map(e => {return (e.description)})
    //     })
    //     setselectedFiles(currentAnnoucement.map(e => {return (e.url)}))
    // } else {
    //     setState({
    //     ...state,
    //     title: '',
    //     description: ''
    //   })
    //   setselectedFiles([])
    //   }

    //eslint-disable-line
  }, [])

//   let libraries = props.masterStore.state.sessionData.AnnoucementList;

//   let currentAnnoucement = libraries && libraries.filter((e) => e.id === Number(props.match.params.id))


  const onChange = (e) => {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  }



  async function handleValidSubmit(e) {
    // setState({...state,
    //   loading: true,
    // });
    // let  userData;
    // userData = props.userStore.fetchUser(props);
    // e.preventDefault()
    // props.masterStore.addAnnoucement(state, userData.access_token, selectedFiles[0])

    // setTimeout(function(){
    //   setState({...state,
    //     title: "",
    //     description: "",
    //     loading: false
    //   })
    // }, 2000)

    // setselectedFiles([])

    setTimeout(function(){
     props.history.push({pathname:"/Annoucement", state:{status: 'created'}});
    }, 2000)

  }

  const handleUpdate = (e) => {
    // setState({...state,
    //   loading: true,
    // });
    // let  userData;
    // userData = props.userStore.fetchUser(props);



    // e.preventDefault()
    // let id = Number(props.match.params.id)

    // props.masterStore.updateAnnoucement(state, userData.access_token, selectedFiles[0], id)

    // setTimeout(function(){
    //   setState({...state,
    //     title: "",
    //     description: "",
    //     loading: false
    //   })
    // }, 2000)
    // setselectedFiles([])

    setTimeout(function(){
     props.history.push({pathname:"/Annoucement", state:{status: 'updated'}});
    }, 2000)

  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Annoucement")}
            breadcrumbItem={props.t("Annoucement")}
          />
           <AvForm
           className="form-horizontal"
           onValidSubmit={(e, v) => {
             handleValidSubmit(e, v);
           }}
           >
            <Row>
              <Col lg="12">
                <Card>
                  <CardBody>


                  <div id="basic-pills-wizard" className="twitter-bs-wizard">
                  <ul className="twitter-bs-wizard-nav nav nav-justified nav-pills">
                    <NavItem>
                      <NavLink
                        className={classnames({active: activeTab === 1})}
                        onClick={() => {
                          setactiveTab(1)
                        }}
                      >
                        <span className="step-number mr-2">01</span>
                        Annoucement Details
                      </NavLink>
                    </NavItem>
                  </ul>
                  <TabContent
                    activeTab={activeTab}
                    className="twitter-bs-wizard-tab-content"
                  >
                    <TabPane tabId={1}>
                     <Row>
                      <Col lg="12">
                        <FormGroup>
                          <Label for="basicpill-firstname-input14">
                            Annoucement Title
                          </Label>
                          <Input
                             value={state.title}
                             onChange={onChange}
                             name="title"
                            type="text"
                            id="basicpill-firstname-input14"
                            className="form-control"
                          />
                        </FormGroup>
                      </Col>
                      </Row>
                      <Row>
                        <Col lg="12">
                        <FormGroup>
                            <Label for="basicpill-address-input2">
                              DESCRIPTION
                            </Label>
                            <textarea
                            //    value={state.description}
                               onChange={onChange}
                              name="description"
                              id="basicpill-address-input2"
                              className="form-control"
                              rows="11"
                            ></textarea>
                          </FormGroup>
                        </Col>

                      </Row>
                    </TabPane>
                  </TabContent>
                  </div>
                  {updateAnnoucement  === "" ?
                    (
                      <div>
                        {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                         type="submit"
                          className="btn btn-warning waves-effect waves-light"
                        >
                          Post Now
                        </button>
                         )}

                      </div>

                    ): (
                      <div>
                        {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                          type="submit"
                          className="btn btn-warning waves-effect waves-light"
                          onClick={handleUpdate}
                        >
                          Update
                        </button>
                         )}

                    </div>
                    )
                  }

                  </CardBody>
                </Card>
              </Col>
            </Row>
          </AvForm>
          </Container>
          </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Annoucement));
