import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, CardBody, CardTitle, Modal, ModalHeader, ModalBody, ModalFooter, Media, Table } from "reactstrap";
import { Link, useLocation, useHistory } from "react-router-dom";
// Pages Components
// import LatestTranaction from "./LatestTranaction";
import stateWrapper from "../../../containers/provider";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from 'react-i18next';

 const QuestionList = (props) => {

    const[state, setState] = useState({
        loading: false,
        alert: ''
      })

      const location = useLocation();
      const history = useHistory();
    useEffect(() => {
        setState({...state, loading: true})
        let userData;
        userData = props.userStore.fetchUser(props);
        async function fetchData(){

          const res = await props.masterStore.fetchQuestion(
            userData.access_token, Number(props.match.params.id)
          );
          if(res.data.status == 'Success'){
            setTimeout(function(){
              setState({...state, loading: false})
            }, 2000)
          }


        }
        fetchData();

        if(location.state && location.state.status) {
            setState({...state, alert: location.state})

            //delete location state
             history.replace({
               pathname: location.pathname, state: {}
             })
           }
        //eslint-disable-line
      }, [])

      let questions = props.masterStore.state.sessionData.questionList;


      const handleDelete = async (id) =>{

        const res = await props.masterStore.deleteQuestion(id);

        if(res.data.status == 'Success'){
         setState({...state, alert: 'deleted'})
         setTimeout(function(){
          setState({...state, alert: ''})
        }, 2000)
        }
        window.location.reload()
     }

          return (
              <React.Fragment>
                <div className="page-content">
                    <Container fluid>

                        {/* Render Breadcrumb */}
                        <Breadcrumbs title={props.t('Question')} breadcrumbItem={props.t('All Question')} />

                        <Row>
                            <Col lg="12">
                            <Card>
        <CardBody>
        <div className="d-flex align-items-center justify-content-between">
          <CardTitle className="mb-4">ALL QUESTIONS</CardTitle>
          <Link to={`/creatquestion/${Number(props.match.params.id)}`}>
                <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2 mb-2"
                         >
                     {" "}
                         Create Question
                </Button>
            </Link>
          </div>
          {state.alert.status == "created" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> New Question created
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}

        {state.alert.status == "updated" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Question updated
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
           {state.alert == "deleted" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Question deleted
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>) : (
                        <div className="table-responsive">
                        <table className="table table-centered table-nowrap mb-0">
                          <thead className="thead-light">
                            <tr>
                              <th>#</th>
                              <th>Question</th>
                              <th>Answer</th>
                              <th>Options</th>
                              <th>Score</th>
                              <th>{" "}</th>
                            </tr>
                          </thead>
                          <tbody>
                            {questions && questions.map((question, key) => (
                              <tr key={"_tr_" + key}>
                                <th scope="row">{key + 1}</th>
                                <td>
                                  <Link to="#" className="text-body font-weight-bold">
                                    {" "}
                                    {question.question}{" "}
                                  </Link>{" "}
                                </td>
                                <td>{question.answer}</td>
                                <td>{question.options}</td>
                                <td>{question.score}</td>
                                <td>

                                <Link to={"/creatquestion/update/" + question.id}>
                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                         >
                     {" "}
                         <i className="bx bx-edit"></i>
                     </Button>
                     </Link>

                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                     onClick={() => handleDelete(question.id)}
                         >
                     {" "}
                      <i className="bx bx-trash"></i>
                     </Button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>

                      </div>
                  )}

        </CardBody>
      </Card>
                            </Col>
                        </Row>
                    </Container>
                </div>
            </React.Fragment>
          );
        }

export default withNamespaces()(stateWrapper(QuestionList));
