import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
} from "reactstrap";
import { Link } from "react-router-dom";

// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";

// React country region selector
import { CountryDropdown } from "react-country-region-selector";

import * as _ from "lodash";
import stateWrapper from "../../../containers/provider";

import modalimage1 from "../../../assets/images/product/img-7.png";
import modalimage2 from "../../../assets/images/product/img-4.png";

// Pages Components
// import LatestTranaction from "./LatestTranaction";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";

const Profile = (props) => {
  const [modal, setmodal] = useState(false);
  const [state, setState] = useState({
    firstName: "",
    lastName: "",
    phone: null,
    email: "",
    country: "",
    password: "extra",
    confirmPassword: "extra",
    success: "",
    loading: false,
  });

  const handleValidSubmit = async (event, values) => {
    console.log(props.userStore.state.sessionData.userId);
      setState({ ...state, loading: true });
      event.preventDefault();
      let getUserId = await props.userStore.state.sessionData.userId;
      console.log(getUserId)
    try {

      console.log("check profile values", values);
      const res = await props.userStore.updateClientProfile(
        state,
        getUserId
      );

      if (res) {
        setState({
          ...state,
          success: "passed",
          loading: false,
        });
      }


      return console.log("check response", res);
    }
    catch(e){
      console.log(e)
    }

  }

  useEffect(() => {
    setState({ ...state, loading: true });
    let userData = props.userStore.fetchUser(props);
    //  let  copySessionData = ({}, this.props.userStore.state.sessionData);
    console.log("user id in profile", userData.userId);
    console.log(props.userStore.state);

    if (!_.isEmpty(userData)) {
      setState({
        ...state,
        loading: false,
        firstName: userData.first_name,
        lastName: userData.last_name,
        phone: userData.phone,
        email: userData.email,
        country: userData.country,
      });
    } else {
      alert("not available");
    }
  }, []);

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("My Profile")}
            breadcrumbItem={props.t("Profile")}
          />
          {state.success == "passed" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Success !
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="mb-4"></div>
            </div>
          ) : (
            ""
          )}
          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                  <CardTitle>PROFILE DETAILS</CardTitle>
                  {state.loading ? (
                    <div class="spinner-border" role="status">
                      <span class="sr-only">Loading...</span>
                    </div>
                  ) : (
                    <AvForm
                      className="form-horizontal"
                      onValidSubmit={(e, v) => {
                        handleValidSubmit(e, v);
                      }}
                    >
                      <Row>
                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input14">
                              First Name
                            </Label>
                            <Input
                              value={state.firstName}
                              onChange={(e) =>
                                setState({
                                  ...state,
                                  firstName: e.target.value,
                                })
                              }
                              type="text"
                              className="form-control"
                              id="basicpill-firstname-input14"
                            />
                          </FormGroup>
                        </Col>

                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input14">
                              Last Name
                            </Label>
                            <Input
                              value={state.lastName}
                              onChange={(e) =>
                                setState({ ...state, lastName: e.target.value })
                              }
                              type="text"
                              className="form-control"
                              id="basicpill-firstname-input14"
                            />
                          </FormGroup>
                        </Col>
                      </Row>

                      <Row>
                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input14">
                              Email
                            </Label>
                            <Input
                              value={state.email}
                              onChange={(e) =>
                                setState({ ...state, email: e.target.value })
                              }
                              placeholder="Enter Email"
                              type="text"
                              className="form-control"
                              id="basicpill-firstname-input14"
                            />
                          </FormGroup>
                        </Col>
                      </Row>

                      <Row>
                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input1">
                              Country
                            </Label>
                            <CountryDropdown
                              className="custom-select"
                              value={state.country}
                              onChange={(e) =>
                                setState({ ...state, country: e.target.value })
                              }
                            />
                          </FormGroup>
                        </Col>
                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input1">
                              Phone
                            </Label>
                            <input
                              value={state.phone}
                              onChange={(e) =>
                                setState({ ...state, phone: e.target.value })
                              }
                              className="form-control"
                              type="number"
                              defaultValue="0"
                              id="example-number-input"
                            />
                          </FormGroup>
                        </Col>
                      </Row>

                      <Row>
                        <Col lg="12">
                          <FormGroup>
                            <Label>CHANGE PASSWORD</Label>
                          </FormGroup>
                        </Col>
                      </Row>

                      <Row>
                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input14">
                              New Password
                            </Label>
                            <Input
                              value={state.password}
                              onChange={(e) =>
                                setState({ ...state, password: e.target.value })
                              }
                              type="password"
                              className="form-control"
                              id="basicpill-firstname-input14"
                            />
                          </FormGroup>
                        </Col>

                        <Col lg="6">
                          <FormGroup>
                            <Label for="basicpill-firstname-input14">
                              Confirm New Password
                            </Label>
                            <Input
                              value={state.confirmPassword}
                              onChange={(e) =>
                                setState({
                                  ...state,
                                  confirmPassword: e.target.value,
                                })
                              }
                              type="password"
                              className="form-control"
                              id="basicpill-firstname-input14"
                            />
                          </FormGroup>
                        </Col>
                      </Row>
                      {/* <Link
                      className="btn btn-primary waves-effect waves-light"
                      to="#"
                    >
                    statement,nd statement , alumni,letter/id card
                      Update Profile
                    </Link> */}
                      <div className=" ">
                        {state.loading ? (
                          <div
                            className="spinner-grow text-primary m-1"
                            role="status"
                          >
                            {/* <span className="sr-only">Loading...</span> */}
                          </div>
                        ) : (
                          <button
                            className="btn btn-primary waves-effect waves-light"
                            type="submit"
                          >
                            Update Profile
