import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from "react-moment";


const SubjectAreaList = (props) => {

  const[subjectAreas, setSubjectAreas] = useState([]);
  const[state, setState] = useState({
    loading: false
  })


  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/subject-area`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setSubjectAreas(res.data.data.rows)
      /*props.subjectStore.fetchSubject();

      let subjectData = await props.subjectStore.state.sessionData.subject;
      console.log(subjectData)
      setSubjectAreas(subjectData)*/


    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    //eslint-disable-line
  }, [])

  function handleDelete(id){
    console.log(id)
     props.subjectStore.deleteSubject(id);

  }


  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <CardTitle className="mb-4">ALL SUBJECT AREA</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>) : (
          <div className="table-responsive">
          <table className="table table-centered table-nowrap mb-0">
            <thead className="thead-light">
              <tr>
                <th style={{ width: "20px" }}>
                  <div className="custom-control custom-checkbox">
                    <input
                      type="checkbox"
                      className="custom-control-input"
                      id="customCheck1"
                    />
                    <label
                      className="custom-control-label"
                      htmlFor="customCheck1"
                    >
                      &nbsp;
                    </label>
                  </div>
                </th>
                <th>Subject Area</th>
                <th>Price</th>
                <th>Created</th>
                <th>Updated</th>
                <th>{" "}</th>
                <th>{" "}</th>
              </tr>
            </thead>
            <tbody>
              {subjectAreas.map((subjectArea, key) => (
                <tr key={"_tr_" + key}>
                  <td>
                    <div className="custom-control custom-checkbox">
                      <input
                        type="checkbox"
                        className="custom-control-input"
                        id={subjectArea.id}
                      />
                      <label
                        className="custom-control-label"
                        htmlFor={subjectArea.id}
                      >
                        &nbsp;
                      </label>
                    </div>
                  </td>
                  <td>
                    <Link to="#" className="text-body font-weight-bold">
                      {" "}
                      {subjectArea.name}{" "}
                    </Link>{" "}
                  </td>
                  <td>{subjectArea.price}</td>
                  <td><Moment format="YYYY/MM/DD">{subjectArea.createdAt}</Moment></td>
                  <td><Moment format="YYYY/MM/DD">{subjectArea.updatedAt}</Moment></td>
                  <td>

                    <Link to={"/admincreatesubjectarea/" + subjectArea.id}>
                      <Button
                      color="primary"
                      className="btn btn-primary waves-effect waves-light mx-2"
                          >
                      {" "}
                          Edit Service
                      </Button>
                      </Link>

                      <Button
                      color="primary"
                      className="btn btn-primary waves-effect waves-light mx-2"
                      onClick={() => handleDelete(subjectArea.id)}
                          >
                      {" "}
                          Delete Service
                      </Button>

                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}


        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(SubjectAreaList));
