import { Container } from "unstated";
import instance from "../helpers/axiosly";
import CONSTANTS from "../helpers/App.constant";
import { setDefaults } from "react-i18next";
import {LOCAL_STORAGE_SIGNIN_KEY} from '../App.constants'


class OrderContainer extends Container {
  constructor() {
    super();
    this.state = {
      sessionData: {
        user: null,
        accessToken: "",
        userId: -1,
        topic: "",
        orderList: [],
        numberOfOrders: 0,
        completedOrders: 0,
        totalBalance: 0,
        orderAdminList: []
      },
    };
  }
  goBack = (props) => {
    props.history.goBack();
  };

  fetchOrders = (userData, bearertoken) => {
    // console.log(userData, bearertoken);
    const token = localStorage.getItem("token")
    // console.log(userData)
    // console.log(token)
    // console.log(bearertoken)
    const userId = userData.id;
    // console.log("check token id", userId);
    instance
      .get(`${process.env.REACT_APP_DATABASEURL}order`, {
        headers: {
          Authorization: `Bearer ${bearertoken}`,
        },
      })
      .then((response) => {
        console.log(response.data.data)
        // count order list by user
        const orderListCount = response.data.data.rows.reduce(
          (counter, obj) => (obj.UserId === userId ? (counter += 1) : counter),
          0
        );
        // Calculate Total balance by user

        // console.log("list", response.data.data.rows);
        let orderList = this.state.sessionData.orderList;

          // console.log(response)
        // console.log("count orders by user :", orderListCount);

        response.data.data.rows.map((obj, i) => {

          if (obj.UserId === userId) {

            // console.log("api for orderList no:", obj.UserId);
            // console.log("api for orderList no:", obj.topic);

            this.setState({
              sessionData: {
                topic: obj.topic,
                numberOfOrders: orderListCount,
                completedOrders: 0,
                totalBalance: obj.balance,
                // newArr.push.apply(newArr, arr)
                orderList: orderList.concat.apply(
                  orderList, response.data.data.rows
                ),
              },
            });
          }
          else{
            console.log("no list for you")
          }
        });


      })

      .catch((e) => console.log(e));
  };

  fetchOrdersAdmin = (bearertoken) => {
    return new Promise((resolve, reject) => {
      instance
      .get(`${process.env.REACT_APP_DATABASEURL}order`, {
        headers: {
          Authorization: `Bearer ${bearertoken}`,
        },
      }).then((response) => {
        resolve(response)
        this.setState({
          sessionData: {
            orderAdminList: response.data.data.rows
          },
        });
      })
    })
  }

  // signIn = ({ email, password }, props) => {
