import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, CardBody, CardTitle, Modal, ModalHeader, ModalBody, ModalFooter, Media, Table } from "reactstrap";

// Pages Components
import { Link } from "react-router-dom";
import instance from "../../helpers/axiosly";
import stateWrapper from "../../containers/provider";
import Moment from "react-moment";


//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";


//i18n
import { withNamespaces } from 'react-i18next';

const ProjectNotification = (props) => {

    const [projectNotifications, setProjectNotifications] = useState([]);
    const [state, setState] = useState({
        loading: false
    })

    useEffect(() => {
        setState({ ...state, loading: true })
        const token = localStorage.getItem("token")
        async function fetchData() {
            try {

                let userData = await props.userStore.fetchUser(props);
                console.log(props?.writerStore?.getProjectNotifications(token, userData?.id))
                console.log(userData, "userData");
                const res = await props?.writerStore?.getProjectNotifications(token, userData?.id)
                setState({ ...state, loading: false })
                setProjectNotifications(res.data.data.rows)
                console.log(res?.data?.data?.rows)
            } catch (e) {
                console.log(e);
            }
        }
        fetchData();
    }, [])

    return (
        <React.Fragment>
            <div className="page-content">
                <Container fluid>

                    {/* Render Breadcrumb */}
                    <Breadcrumbs title={props.t('ProjectNotification')} breadcrumbItem={props.t('All ProjectNotification')} />

                    <Row>
                        <Col lg="12">
                            <Card>
                                <CardBody>
                                    <CardTitle className="mb-4">ALL PROJECTS</CardTitle>
                                    {state.loading ? (<div class="spinner-border" role="status">
                                        <span class="sr-only">Loading...</span>
                                    </div>) : (
                                        <div className="table-responsive">
                                            <table className="table table-centered table-nowrap mb-0">
                                                <thead className="thead-light">
                                                    <tr>
                                                        <th style={{ width: "20px" }}>
                                                            <div className="custom-control custom-checkbox">
                                                                <input
                                                                    type="checkbox"
                                                                    className="custom-control-input"
                                                                    id="customCheck1"
                                                                />
                                                                <label
                                                                    className="custom-control-label"
                                                                    htmlFor="customCheck1"
                                                                >
                                                                    &nbsp;
                                                                </label>
                                                            </div>
                                                        </th>
                                                        <th>Project Topic</th>
                                                        <th>Urgency</th>
                                                        <th>Actions</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                {projectNotifications.map((project, key) => (
                              <tr key={"_tr_" + key}>
                                <td>
                                  <div className="custom-control custom-checkbox">
                                    <input
                                      type="checkbox"
                                      className="custom-control-input"
                                    //   id={project.count}
                                    />
                                    <label
                                      className="custom-control-label"
                                    //   htmlFor={project.count}
                                    >
                                      &nbsp;
                                    </label>
                                  </div>
                                </td>
                                {/* <td>
                                  <Link to="#" className="text-body font-weight-bold">
                                    {" "}
                                    {project.order.topic}{" "}
                                  </Link>{" "}
                                </td> */}
                                {/* <td><Moment format="YYYY/MM/DD">{project.order.createdAt}</Moment> </td> */}
                                {/* <td>{project.order.urgency}</td> */}
                                {/* <td>
                                  <Link to={"/wprojectspage/" + project.id}>
                                    <Button
                                    color="primary"
                                    className="btn btn-primary waves-effect waves-light"
                                        >
                                    {" "}
                                        View Projects
                                    </Button>
                                  </Link>
                                </td> */}
                              </tr>
                            ))}
                                                    {/* <button
                            className="btn btn-primary waves-effect waves-light"
                            type="button"
                          onClick={() => { downloadFile(x.order.additional_materials) }}
                          >
                            <i class="mdi mdi-download font-size-16 align-middle me-2"></i>
                            {" "}Download pdf
                              </button> */}
                                                </tbody>
                                            </table>
                                        </div>
                                    )}

                                </CardBody>
                            </Card>

                        </Col>
                    </Row>
                </Container>
            </div>
        </React.Fragment>
    );
}

export default withNamespaces()(stateWrapper(ProjectNotification));
