import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Media,
  Table,
} from "reactstrap";
import { Link } from "react-router-dom";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import instance from "../../helpers/axiosly";
import stateWrapper from "../../containers/provider";


const Payments = (props) => {

  const [projectArray, setProjecArray] = useState([]);
  const [state, setState] = useState({
    project: "",
    total_balance: 0
  })
  useEffect(() => {
    async function fetchData() {
      try {
        const token = localStorage.getItem("token")
        const res = await instance
          .get(`${process.env.REACT_APP_DATABASEURL}/order`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          })

        setProjecArray(res.data.data.rows)

        // get the total balance for all projects
        let projectTotalBalance = res.data.data.rows.reduce(function (previousValue, currentValue) {
          return {
            balance: parseInt(previousValue.balance) + parseInt(currentValue.balance)
          }
        });
        setState({ total_balance: projectTotalBalance.balance })

        console.log("projects for this user :".res.data.data.rows)


      } catch (e) {
        console.log(e);
      }
    }
    fetchData();


    //eslint-disable-line
  }, [])


  const onChange = (e) => {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Payments")}
            breadcrumbItem={props.t("Payments")}
          />

          <Row>
            <Col lg="8">
              <Card>
                <CardBody>
                  <h5>Payments</h5>
                  <p>Separate Existence is a myth</p>
                  <div className="hr"></div>

                  <div className="mb-4"></div>
                  <Row>
                    <Col lg="5">
                      <div class="card-radio-custom ">
                        <i class="mdi mdi-bitcoin font-size-24 text-warning align-middle mr-2"></i>
                        <span>
                          {/* Total Balance : <b>{`$${props.orderStore.state.sessionData.totalBalance}`} </b> */}
                          Total Balance : <b>{`$${state.total_balance}`} </b>

                        </span>
                      </div>
                      <div className="mb-4"></div>
                    </Col>
                  </Row>
                  <Row>
                    <Col lg="6">
                      <p>
                        Flutterwave is an online payment gateway that allows
                        merchants and banks to accept, manage and process
                        digital payments.{" "}
                      </p>
                    </Col>
                  </Row>

                  <div className="mb-4"></div>
                  <Row>
                    <Col lg="4">
                      <select
                        className="custom-select"
                        value={state.project}
                        name="project"
                        onChange={onChange}
                      >
                        <option defaultValue>Select </option>
                        {projectArray.map((item, id) => {
                          return (
                            <option value={item.id} key={id}>
                              {item.topic}
                            </option>
                          );
                        })}
                      </select>
                    </Col>
                    <Col lg="4">
                      <Button
                        color="primary"
                        className="btn btn-warning waves-effect waves-light"
                      >
                        {" "}
                    Pay All
                  </Button>
                    </Col>
                  </Row>

                </CardBody>
              </Card>
            </Col>
            <Col lg="4">
              {state.project === '' ? '' :
                <Card>
                  <CardBody>
                    {projectArr()?.topic}

                    <div className="mb-4"></div>

                    <Button
                      color="primary"
                      className="btn btn-warning waves-effect waves-light"
                    >
                      {" "}
                    Pay Balance
                  </Button>
                  </CardBody>
                </Card>
              }

            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Payments));
