require('dotenv').config();
export const ENV = process.env.REACT_APP_ENV;

console.log("env == ", process.env)

const isProduction = ENV.toLowerCase() === 'production';
const isLocalHost = ENV.toLowerCase() === 'development';



const stagingConfig = {
  flutterwaveSecret: process.env.FLUTTERWAVE_SECRET,
  fluterwavePublicKey: process?.env?.FLUTTERWAVE_PUBLIC_KEY

};

const localConfig = {
    flutterwaveSecret: process.env.FLUTTERWAVE_SECRET, 
    fluterwavePublicKey: process?.env?.FLUTTERWAVE_PUBLIC_KEY
};

const productionConfig = {
    flutterwaveSecret: process.env.FLUTTERWAVE_SECRET,
    fluterwavePublicKey: process?.env?.FLUTTERWAVE_PUBLIC_KEY
};


export const config = isProduction ? productionConfig : isLocalHost ?  localConfig : localConfig;
