import React, { useState, useEffect } from "react";
import {
  Card,
  CardBody,
  Container,
  CardTitle,
  Badge,
  Row,
  Col,
  TabContent,
  TabPane,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
  Progress,
} from "reactstrap";

import instance from "../../helpers/axiosly";

// formik
// import {useFormik } from 'formik';

import { Link } from "react-router-dom";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";

// style
import style from "./style.scss";

// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";

// Dropzone
import Dropzone from "react-dropzone";
import axios from "axios";

import classnames from "classnames";

import stateWrapper from "../../containers/provider";

const NewOrder = (props) => {
  // console.log(props)
  // const FormData = require('form-data');

  const [activeTab, setactiveTab] = useState(1);
  const [activeTabProgress, setactiveTabProgress] = useState(1);
  const [progressValue, setprogressValue] = useState(25);
  const [selectedFiles, setselectedFiles] = useState([]);

  const serviceTypeArray = [
    {
      id: 0,
      price: 20,
      name: "Academic Writing",
    },
    {
      id: 1,
      price: 10,
      name: "Resume",
    },
    {
      id: 2,
      price: 50,
      name: "Proof Reading",
    },
  ];

  const typeOfPaperArray = [
    {
      id: 0,
      price: 20,
      name: "Essay ",
    },
    {
      id: 1,
      price: 50,
      name: "Thesis",
    },
  ];

  const urgencyArray = [
    {
      id: 0,
      price : 20,
      name : "3 days"
    },
    {
      id: 2,
      price : 10,
      name : "I week"
    },
    {
      id: 3,
      price : 5,
      name : "2 weeks"
    }
  ];

  const pagesPriceArray = [
    {
      id: 0,
      price: 10,
      name: "firstRange"
    },
    {
      id: 1,
      price: 25,
      name: "secondRange"
    },
    {
      id: 2,
      price: 35,
      name: "thirdRange"
    }
  ];

  const [state, setState] = useState({
    projectTopic: "",
    serviceType: null,
    paperType: null,
    subjectArea: "",
    paperFormat: "",
    academicLevel: "",
    numberOfWords: 0,
    paperDescription: "",
    additionalMaterial: "",
    urgency: null,
    spacing: 0,
    fontSize: 0,
    completePayment: "localhost:3000",
    //total: 0,
    pagePrice: null,
    urgencyPrice: 0,
    servicePrice: 0,
    loading: false
  });

  const [total, setTotal] = useState(0)

  const serviceName = serviceTypeArray.findIndex((item) => {
    return item.id == state.serviceType
  });
  const servicename = (serviceTypeArray[serviceName !== -1 ? serviceName : '0'].name)

  const paperTypeName = typeOfPaperArray.findIndex((item) => {
    return item.id == state.paperType
  });
  const papertypename = (typeOfPaperArray[paperTypeName !== -1 ? paperTypeName : '0'].name)


  const onChange = (e) => {
    let { name, value } = e.target;

    // let tots = state.total ;
    // tots = parseInt(state.serviceType) + parseInt(state.paperType) + parseInt(state.subjectArea) + parseInt(state.urgency);
    // console.log("tots here",tots)
    // setState({
    //   ...state,
    // total:tots
