import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Media,
  Table,
} from "reactstrap";
import { Link } from "react-router-dom";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import instance from "../../helpers/axiosly";
import stateWrapper from "../../containers/provider";


const Wallet = (props) => {


  useEffect(() => {



    //eslint-disable-line
  }, [])





  const onChange = (e) => {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Wallet")}
            breadcrumbItem={props.t("Wallet")}
          />

          <Row>
            <Col lg="8">
              <Card>
                <CardBody>
                  <h5>Wallet</h5>
                  <p>Separate Existence is a myth</p>
                  <div className="hr"></div>

                  <div className="mb-4"></div>
                  <Row>
                    <Col lg="5">
                      <div class="card-radio-custom ">
                        <i class="mdi mdi-bitcoin font-size-24 text-warning align-middle mr-2"></i>
                        <span>
                          {/* Total Balance : <b>{`$${props.orderStore.state.sessionData.totalBalance}`} </b> */}
                          Total Balance : <b>{`$${state.total_balance}`} </b>

                        </span>
                      </div>
                      <div className="mb-4"></div>
                    </Col>
                  </Row>
                  <Row>
                    <Col lg="6">
                      <p>
                        Flutterwave is an online payment gateway that allows
                        merchants and banks to accept, manage and process
                        digital Wallet.{" "}
                      </p>
                    </Col>
                  </Row>

                  <div className="mb-4"></div>
                  <Row>
                    <Col lg="4">
                      <select
                        className="custom-select"
                        value={state.project}
                        name="project"
                        onChange={onChange}
                      >
                        <option defaultValue>Select </option>
                        {projectArray.map((item, id) => {
                          return (
                            <option value={item.id} key={id}>
                              {item.topic}
                            </option>
                          );
                        })}
                      </select>
                    </Col>
                    <Col lg="4">
                      <Button
                        color="primary"
                        className="btn btn-warning waves-effect waves-light"
                      >
                        {" "}
                    Pay All
                  </Button>
                    </Col>
                  </Row>

                </CardBody>
              </Card>
            </Col>
            <Col lg="4">
              {state.project === '' ? '' :
                <Card>
                  <CardBody>
                    <p>
                      Project Topic :</p>
                    <p>
                      {projectArr()?.topic}
                    </p>
                    <p>
                      Project Balance :</p>
                    <p>
                      ${projectArr()?.balance}
                    </p>

                    <div className="mb-4"></div>

                    <Button
                      color="primary"
                      className="btn btn-warning waves-effect waves-light"
                    >
                      {" "}
                    Pay ${projectArr()?.balance}
                  </Button>
                  </CardBody>
                </Card>
              }

            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Wallet));
