import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button, Container, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import Moment from 'react-moment'
import Pagination from './Pagination'

const Users = (props) => {

  //const[users, setUsers] = useState([])
  const[currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(10)
  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {
    let mounted = true;
    setState({...state, loading: true})
    let userData;
    userData = props.userStore.fetchUser(props);

    async function fetchData(){

      const res = await props.userStore.fetchAllUser(
        userData.access_token
      );
      if(res.data.status == 'Success'){
          setState({...state, loading: false})
      }

    }
    fetchData();
    return () => mounted = false;
    //eslint-disable-line
  }, [])

  let users = props.userStore.state.sessionData.allUserList;

  const clients = users && users.filter(user => user.RoleId === 2)

  //get current page
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOffirstUser = indexOfLastUser - usersPerPage;
  const currentClient = clients && clients.slice(indexOffirstUser, indexOfLastUser);
  //change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  return (
    <React.Fragment>
         <div className="page-content">
                    <Container fluid>

                        {/* Render Breadcrumb */}
                        <Breadcrumbs title={props.t('My Users')} breadcrumbItem={props.t('Clients')} />

                        <Row>
                            <Col lg="12">
                                <Card>
        <CardBody>
          <CardTitle className="mb-4">Users</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>): (
         <div className="table-responsive">
         <table className="table table-centered table-nowrap mb-0">
           <thead className="thead-light">
             <tr>
             <th>#</th>
               <th>User ID</th>
               <th>Created At</th>
               <th>Name</th>
               <th>Email</th>
               <th>Role</th>
             </tr>
           </thead>
           <tbody>
             {currentClient && currentClient.map((client, key) => (
               <tr key={"_tr_" + key}>
                 <th scope="row">{key + 1}</th>
                 <td>
                   <Link to="#" className="text-body font-weight-bold">
                     {" "}
                     {client.id}{" "}
                   </Link>{" "}
                 </td>
                 <td><Moment format="YYYY/MM/DD">{client.createdAt}</Moment></td>
                 <td>{client.first_name} {client.last_name}</td>
                 <td>{client.email}</td>
                 <td>Client</td>
               </tr>
             ))}
           </tbody>
         </table>
         <Pagination usersPerPage={usersPerPage} totalUser={users && users.length} paginate={paginate}/>
       </div>

      )}

        </CardBody>
      </Card>
                            </Col>
                        </Row>
                    </Container>
                </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Users));
