import React, { useEffect, useState } from "react";
import { Route, Redirect, withRouter } from "react-router-dom";
import { LOCAL_STORAGE_SIGNIN_KEY } from '../../App.constants'
import { grantPermission } from '../../GrantPermission'
import { ROLES } from '../../App.constants';
import stateWrapper from '../../containers/provider';

function Writermiddleware({
	component: Component, layout: Layout, roles, path,  ...rest
}) {
	const [state, setState] = useState(false);
	useEffect(() => {
		(async () => {
			const userData = rest?.userStore?.fetchUser();
			const bearertoken = userData?.access_token;
			const userId = userData?.id
			console.log(userData, bearertoken);
			const writerExamStatus = await rest?.masterStore?.findWriterExam(userId, bearertoken);
			 if (writerExamStatus?.status == 'Success') {
				 setState(true);
				 return;
			 } else {
				 setState(false);
				 return
			 }
			console.log(writerExamStatus);
		})()

	}, [])
	return (
		<>
			{ grantPermission([ROLES.WRITER]) && (
					(!state && path == 'wexam') && (
						<Route
						{...rest}
						//   findWriterExam
						render={(props) => (
							<Layout>
								<Component {...props} />
							</Layout>
						)}
					/>
					)

			)}
			{
				!grantPermission([ROLES.WRITER]) && (
					<Route
						{...rest}
						render={(props) => (
							<Redirect to={{ pathname: "/login", state: { from: props.location } }} />
						)}

					/>

				)
			}
		</>
	);
}

export default withRouter(stateWrapper(Writermiddleware));
