import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button, Card, CardBody, CardTitle, Modal, ModalHeader, ModalBody, ModalFooter, Media, Table } from "reactstrap";


//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";
//i18n
import { withNamespaces } from 'react-i18next';
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import Moment from "react-moment";

 const Support = (props) => {

    const[state, setState] = useState({
        loading: false
      })


    useEffect(() => {

        setState({...state, loading: true})
        async function fetchData(){
          let userData;
        userData = props.userStore.fetchUser(props);

        try {
          const token = localStorage.getItem("token")
          const res = props.masterStore.fetchSuppprt(
            userData.access_token
             );
             setState({...state, loading: false})
        } catch (e) {
          console.log(e);
        }
        }
        fetchData();

        //eslint-disable-line
      }, [])

      let supports = props.masterStore.state.sessionData.supportList;
      console.log(supports)

          return (
              <React.Fragment>
                <div className="page-content">
                    <Container fluid>

                        {/* Render Breadcrumb */}
                        <Breadcrumbs title={props.t('Support')} breadcrumbItem={props.t('support')} />

                        <Row>
                            <Col lg="12">
                            <Card>
        <CardBody>
          <CardTitle className="mb-4">SUPPORTS</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):
          (
            <div>
          <div className="table-responsive">
            <table className="table table-centered table-nowrap mb-0">
              <thead className="thead-light">
                <tr>
                  <th>#</th>
                  <th>Title</th>
                  <th>Body</th>
                  <th>Name</th>
                  <th>created At</th>
                </tr>
              </thead>
              <tbody>
                {supports && supports.map((support, key) => (
                  <tr key={"_tr_" + key}>
                    <th scope="row">{key + 1}</th>
                    <td>
                      <Link to="#" className="text-body font-weight-bold">
                        {" "}
                        {support.title}{" "}
                      </Link>{" "}
                    </td>
                    <td>{support.body}</td>
                    <td>{support.User.first_name} {support.User.last_name}</td>
                    <td><Moment format="YYYY/MM/DD">{support.createdAt}</Moment></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
            </div>
          )}
        </CardBody>
      </Card>
                            </Col>
                        </Row>
                    </Container>
                </div>
            </React.Fragment>
          );
        }

export default withNamespaces()(stateWrapper(Support));
