import React, { useEffect, useState } from "react";
import {  Container, Row, Col, Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import Pagination from './Pagination'

const Users = (props) => {

  const[users, setUsers] = useState([]);
  const[orders, setOrders] = useState([])

  const [state, setState] = useState({
    userId: null,
    orderId: null,
    loading: false
  })
  const[currentPage, setCurrentPage] = useState(1);
  const [orderPerPage, setUsersPerPage] = useState(4)

  //get current page
  const indexOfLastOrder = currentPage * orderPerPage;
  const indexOffirstOrder = indexOfLastOrder - orderPerPage;
  const currentOrders = orders.slice(indexOffirstOrder, indexOfLastOrder);
  //change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){ 
    try {
      const token = localStorage.getItem("token")
      const userRes = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setUsers(userRes.data.data.rows)

        const orderRes = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/order`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })

        setOrders(orderRes.data.data.rows)

    } catch (e) {
      console.log(e);
    } 
    }
    fetchData();
    //eslint-disable-line
  }, [])

  const writers = users.filter(user => user.RoleId === 3)
  const clients = users.filter(user => user.RoleId === 2)


  const [search, setsearch] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();

      try {
        const formData = new FormData();
        const token = localStorage.getItem("token")
        console.log(state.userId)
        formData.append("user_id", state.userId);
        formData.append("order_id", state.orderId); //state.serviceType); 


        await instance
            .post(`${process.env.REACT_APP_DATABASEURL}/project`,  formData, {
              headers: {
                "Content-Type": "multipart/form-data",
                Authorization: `Bearer ${token}`,
              },
            }).then((response) => {
                console.log(response.data.data)})
                .catch((e) => console.log(e));

      }
       catch (e) {
        console.log(e);
      }
  }

  return (
    <React.Fragment>
        <div className="page-content">
        <Container fluid>
        <Row>
        <Col lg="12">
         {/* Render Breadcrumb */}
         <Breadcrumbs
            title={props.t("Project")}
            breadcrumbItem={props.t("Assign Project")}
          />
      <Card>
        <CardBody>
          {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):(
                       <form onSubmit={handleSubmit}>
                       <div className="p-0"
                               aria-labelledby="page-header-search-dropdown">
                               <form className="p-3">
                                 <div className="form-group m-0">
                                   <div className="input-group">
                                     <input type="text" className="form-control" placeholder="Search..." aria-label="Recipient's username" />
                                     <div className="input-group-append">
                                       <button className="btn btn-primary" type="submit"><i className="mdi mdi-magnify"></i></button>
                                     </div>
                                   </div>
                                 </div>
                               </form>
                             </div>
                       <div className="row">
                       <div className="table-responsive col">
                         <table className="table">
                           <thead className="thead-light">
                             <tr>
                               <th style={{ width: "20px" }}>
                                 {" "}
                               </th>
                               <th>Writer</th>
                             </tr>
                           </thead>
                           <tbody>
                             {writers.map((writer, key) => (
                               <tr>
                                 <td>
                                   <div className="custom-control custom-checkbox">
                                     <input
                                       type="checkbox"
                                       className="custom-control-input"
                                       id={writer.id}
                                       value={state.userId}
                                       onChange={e => setState({...state, userId: writer.id})}
                                     />
                                     <label
                                       className="custom-control-label"
                                       htmlFor={writer.id}
                                     >
                                       &nbsp;
                                     </label>
                                   </div>
                                 </td>
                                 <td>{writer.first_name} {writer.last_name}</td>      
                               </tr>
                             ))}

                           </tbody>
                         </table>
                       </div>
                      <div className="table-responsive col">
                      <table className="table">
                           <thead className="thead-light">
                             <tr>
                               <th>Client</th>
                             </tr>
                           </thead>
                           <tbody>
                             {clients.map((client, key) => (
                               <tr>
                                 <td>{client.first_name} {client.last_name}</td>       
                               </tr>
                             ))}

                           </tbody>
                         </table>
                      </div>
                      <div className="col">
                      <table className="table">
                           <thead className="thead-light">
                             <tr className="table-primary" onClick={() => { setsearch(!search) }}>
                               <th style={{ width: "20px" }}>
                                 {" "}
                               </th>
                               <th>
                                     Orders
                               </th>
                             </tr>
                           </thead>
                           <tbody>
                               {currentOrders.map((order, key) => ( 
                               <tr>
                                 <td>
                                   <div className="custom-control custom-checkbox">
                                     <input
                                       type="checkbox"
                                       className="custom-control-input"
                                       id={order.id}
                                       value={state.orderId}
                                       onChange={e => setState({...state, orderId: order.id})}
                                     />
                                     <label
                                       className="custom-control-label"
                                       htmlFor={order.id}
                                     >
                                       &nbsp;
                                     </label>
                                   </div>
                                 </td>
                                 <td>{order.topic}</td>        
                               </tr>  
                                  ))}                          
                           </tbody>

                         </table>
                         <Pagination orderPerPage={orderPerPage} totalOrder={orders.length} paginate={paginate}/> 
                      </div>
                      <div className="col"> 
                                 <Button
                                 type="submit"
                                 color="primary"
                                 className="btn btn-primary waves-effect waves-light"
                                   >
                                   {" "}
                                  Assign Project
                                 </Button>

                      </div>
                      </div>



                       </form>
                  )}


        </CardBody>
        </Card>
        </Col>
        </Row>
    </Container>
            </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Users));
