import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link, useLocation, useHistory } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from "react-moment";
import Pagination from './Pagination'

const ExamList = (props) => {

  const[state, setState] = useState({
    loading: false,
    alert: ''
  })

  const location = useLocation();
  const history = useHistory();

  const[currentPage, setCurrentPage] = useState(1);
  const [examPerPage, setExamPerPage] = useState(10)



  useEffect(() => {
    setState({...state, loading: true})
    let userData;
    userData = props.userStore.fetchUser(props);
    async function fetchData(){

      const res = await props.masterStore.fetchExam(
        userData.access_token
      );
      if(res.data.status == 'Success'){
        setTimeout(function(){
          setState({...state, loading: false})
        }, 2000)
      }


    }
    fetchData();

    if(location.state && location.state.status) {
        setState({...state, alert: location.state})

        //delete location state
         history.replace({
           pathname: location.pathname, state: {}
         })
       }
    //eslint-disable-line
  }, [])

  let exams = props.masterStore.state.sessionData.examList;

  const indexOfLastExam = currentPage * examPerPage;
  const indexOffirstExam = indexOfLastExam - examPerPage;
  const currentExam = exams && exams.slice(indexOffirstExam, indexOfLastExam);
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  const handleDelete = async (id) =>{

    const res = await props.masterStore.deleteExam(id);

    if(res.data.status == 'Success'){
     setState({...state, alert: 'deleted'})
     setTimeout(function(){
      setState({...state, alert: ''})
    }, 2000)
    }
    window.location.reload()
 }

  return (
    <React.Fragment>

      <Card>
        <CardBody>
        <div className="d-flex align-items-center justify-content-between">
          <CardTitle className="mb-4">ALL EXAMS</CardTitle>
          <Link to={"/createxam"}>
                <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2 mb-2"
                         >
                     {" "}
                         Create Exam
                </Button>
            </Link>
          </div>
          {state.alert.status == "created" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> New Exam created
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}

        {state.alert.status == "updated" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Exam updated
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
           {state.alert == "deleted" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Exam deleted
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>) : (
                        <div className="table-responsive">
                        <table className="table table-centered table-nowrap mb-0">
                          <thead className="thead-light">
                            <tr>
                              <th>#</th>
                              <th>Exam Topic</th>
                              <th>Created At</th>
                              <th>Updated At</th>
                              <th>{" "}</th>
                            </tr>
                          </thead>
                          <tbody>
                            {currentExam && currentExam.map((exam, key) => (
                              <tr key={"_tr_" + key}>
                                <th scope="row">{key + 1}</th>
                                <td>
                                  <Link to="#" className="text-body font-weight-bold">
                                    {" "}
                                    {exam.name}{" "}
                                  </Link>{" "}
                                </td>
                                <td><Moment format="YYYY/MM/DD">{exam.createdAt}</Moment></td>
                                <td><Moment format="YYYY/MM/DD">{exam.updatedAt}</Moment> </td>
                                <td>
                                  <Link to={"/question/" + exam.id}>
                                    <Button
                                    color="primary"
                                    className="btn btn-primary waves-effect waves-light"
                                        >
                                    {" "}
                                        View Questions
                                    </Button>
                                  </Link>

                                <Link to={"/createxam/" +exam.id}>
                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                         >
                     {" "}
                         <i className="bx bx-edit"></i>
                     </Button>
                     </Link>

                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                     onClick={() => handleDelete(exam.id)}
                         >
                     {" "}
                      <i className="bx bx-trash"></i>
                     </Button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                        <Pagination examPerPage={examPerPage} totalExam={exams && exams.length} paginate={paginate}/>
                      </div>
                  )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(ExamList));
