import React from "react";
import { Row, Col, CardBody, Card, Alert, Container } from "reactstrap";
// Redux
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";
// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";
// actions
import stateWrapper from "../../containers/provider";
// import images
import profile from "../../assets/images/profile-img.png";
import logo from "../../assets/images/logo.svg";

import { validateEmailAddress } from "../../helpers/utility/fieldValidator";
import { EMAIL_REGEX } from '../../helpers/utility/regexes';

import {
  ENTER_VALID_EMAIL_ERROR_TEXT,
  FIELD_IS_REQUIRED_ERROR_TEXT,
} from "../../helpers/utility/Validate.message";

const Login = (props) => {

  const validateEmailField = (rule, value, callback) => {
    if (value === "" || value == null) {
      callback(FIELD_IS_REQUIRED_ERROR_TEXT);
      alert("sucks")
    }
    if (!validateEmailAddress(value)) {
      callback(ENTER_VALID_EMAIL_ERROR_TEXT);
    }
    callback();
  };

  // handleValidSubmit
  function handleValidSubmit(event, values) {
    //   event.preventDefault();

    // props.loginUser(values, props.history);
    props.userStore.signIn(values, props);
  }
  return (
    <React.Fragment>
      <div className="home-btn d-none d-sm-block">
        <Link to="/" className="text-dark">
          <i className="fas fa-home h2"></i>
        </Link>
      </div>
      <div className="account-pages my-5 pt-sm-5">
        <Container>
          <Row className="justify-content-center">
            <Col md={8} lg={6} xl={5}>
              <Card className="overflow-hidden">
                <div className="bg-soft-primary">
                  <Row>
                    <Col className="col-7">
                      <div className="text-primary p-4">
                        <h5 className="text-primary">Welcome Back !</h5>
                        <p>Sign in to continue to AcadWritings.</p>
                      </div>
                    </Col>
                    <Col className="col-5 align-self-end">
                      <img src={profile} alt="" className="img-fluid" />
                    </Col>
                  </Row>
                </div>
                <CardBody className="pt-0">
                  <div>
                    <Link to="/">
                      <div className="avatar-md profile-user-wid mb-4">
                        <span className="avatar-title rounded-circle bg-light">
                          <img
                            src={logo}
                            alt=""
                            className="rounded-circle"
                            height="34"
                          />
                        </span>
                      </div>
                    </Link>
                  </div>
                  <div className="p-2">
                    <AvForm
                      className="form-horizontal"
                      onValidSubmit={(e, v) => {
                        handleValidSubmit(e, v);
                      }}
                    >
                      {props.error && props.error ? (
                        <Alert color="danger">{props.error}</Alert>
                      ) : null}

                      <div className="form-group">
                        <AvField
                          name="email"
                          label="Email"
                          value=" "
                          className="form-control"
                          placeholder="Enter email"
                          type="email"
                          errorMessage= {ENTER_VALID_EMAIL_ERROR_TEXT}
                          // rules={[{ validator: validateEmailField, required: true }]}
                          validate={{pattern: {value: /^[A-Z]*$/}}}
                          validate={{
                            required: {value: true},
                            pattern: {value: EMAIL_REGEX},
                            async: {validateEmailField}
                            // minLength: {value: 6},
                            // maxLength: {value: 16}
                          }}
                          required
                        />
                      </div>

                      <div className="form-group">
                        <AvField
                          name="password"
                          label="Password"
                          value=""
                          type="password"
                          required
                          placeholder="Enter Password"
                        />
                      </div>

                      <div className="custom-control custom-checkbox">
                        <input
                          type="checkbox"
                          className="custom-control-input"
                          id="customControlInline"
                        />
                        <label
                          className="custom-control-label"
                          htmlFor="customControlInline"
                        >
                          Remember me
                        </label>
                      </div>

                      <div className="mt-3">
                        <button
                          className="btn btn-primary btn-block waves-effect waves-light"
                          type="submit"
                        >
                          Log In
                        </button>
                      </div>

                      <div className="mt-4 text-center">
                        <Link to="/forgot-password" className="text-muted">
                          <i className="mdi mdi-lock mr-1"></i> Forgot your
                          password?
                        </Link>
                      </div>
                    </AvForm>
                  </div>
                </CardBody>
              </Card>
              <div className="mt-5 text-center">
                <p>
                  Don't have an account ?{" "}
                  <Link
                    to="register"
                    className="font-weight-medium text-primary"
                  >
                    {" "}
                    Signup now{" "}
                  </Link>{" "}
                </p>
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};



export default withRouter((stateWrapper(Login)));
