import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import Moment from 'react-moment'
import Pagination from './Pagination'

const Users = (props) => {

  const[users, setUsers] = useState([])
  const[currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(10)
  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {
    let mounted = true;
    setState({...state, loading: true})
    async function fetchData(){ 

    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setUsers(res.data.data.rows)
    } catch (e) {
      console.log(e);
    } 
    } 
    fetchData();
    return () => mounted = false;
    //eslint-disable-line
  }, [])

  //get current page
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOffirstUser = indexOfLastUser - usersPerPage;
  const currentUser = users.slice(indexOffirstUser, indexOfLastUser);
  //change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <CardTitle className="mb-4">Users</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>): (
         <div className="table-responsive">
         <table className="table table-centered table-nowrap mb-0">
           <thead className="thead-light">
             <tr>
               <th style={{ width: "20px" }}>
                 <div className="custom-control custom-checkbox">
                   <input
                     type="checkbox"
                     className="custom-control-input"
                     id="customCheck1"
                   />
                   <label
                     className="custom-control-label"
                     htmlFor="customCheck1"
                   >
                     &nbsp;
                   </label>
                 </div>
               </th>
               <th>User ID</th>
               <th>Created At</th>
               <th>Name</th>
               <th>Email</th>
               <th>Role</th>
             </tr>
           </thead>
           <tbody>
             {currentUser.map((user, key) => (
               <tr key={"_tr_" + key}>
                 <td>
                   <div className="custom-control custom-checkbox">
                     <input
                       type="checkbox"
                       className="custom-control-input"
                       id={user.id}
                     />
                     <label
                       className="custom-control-label"
                       htmlFor={user.id}
                     >
                       &nbsp;
                     </label>
                   </div>
                 </td>
                 <td>
                   <Link to="#" className="text-body font-weight-bold">
                     {" "}
                     {user.id}{" "}
                   </Link>{" "}
                 </td>
                 <td><Moment format="YYYY/MM/DD">{user.createdAt}</Moment></td>
                 <td>{user.first_name} {user.last}</td>
                 <td>{user.email}</td>
                 {user.RoleId === 1 ? <td>Admin</td>: <td>User</td>}
               </tr>
             ))}
           </tbody>
         </table>
         <Pagination usersPerPage={usersPerPage} totalUser={users.length} paginate={paginate}/>
       </div>

      )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Users));
