import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Media,
  Table,
} from "reactstrap";
import { Link } from "react-router-dom";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";


const Wallet = (props) => {

  const [state, setState] = useState({
    requestStatus: "",
    showAlert: false
  })
  useEffect(() => {
    // fetch user data
    const sendRequest = async () => {
      let userData = await props?.userStore?.state?.sessionData?.user
      console.log(userData)
      let amount = "20";
      let bearertoken = userData?.access_token;
      console.log(bearertoken)
      let send = await props?.withdrawalStore?.requestWithdrawal(bearertoken, amount);
      console.log(send)

      let status = props.withdrawalStore

      setState({
        requestStatus:'',
        showAlert: true
      })

      // accessToken: "",
      //     userId: -1,
    }
  }, [])

  // function to send withdrawal request




  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Wallet")}
            breadcrumbItem={props.t("Wallet")}
          />

          {state.showAlert ? (

            <div>
              { state.requestStatus === "success" ? (
              <div
              className="alert alert-info alert-dismissible fade show"
              role="alert"
            >
              <i className="mdi mdi-alert-circle-outline mr-2"></i> Withdrawal Request has been submitted successfully!
            <button
                type="button"
                class="close"
                data-dismiss="alert"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
              ) : (
                <div
                className="alert alert-warning alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> You don't have sufficient funds in your wallet !
              <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              ) }

              <div className="mb-4"></div>
            </div>

          ) :( '')}


          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                  <h5>Wallet</h5>
                  <p>Separate Existence is a myth</p>
                  <div className="hr"></div>

                  <div className="mb-4"></div>
                  <Row>
                    <Col lg="5">
                      <div class="card-radio-custom ">
                        <i class="mdi mdi-bitcoin font-size-24 text-warning align-middle mr-2"></i>
                        <span>
                          {/* Total Balance : <b>{`$${props.orderStore.state.sessionData.totalBalance}`} </b> */}
                           Balance : <b>$0 </b>

                        </span>
                      </div>
                      <div className="mb-4"></div>
                    </Col>
                  </Row>
                  <Row>
                    <Col lg="6">
                      <p>
                        Flutterwave is an online payment gateway that allows
                        merchants and banks to accept, manage and process
                        digital Wallet.{" "}
                      </p>
                    </Col>
                  </Row>

                  <div className="mb-4"></div>
                  <Row>
                    <Col lg="4">
                      <Button
                        color="primary"
                        className="btn btn-warning waves-effect waves-light"
                        onClick={sendRequest}
                      >
                        {" "}
                    Request Withdrawal
                  </Button>
                    </Col>
                  </Row>

                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Wallet));
