import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, CardBody, CardTitle } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from 'react-moment'


const Orderpage = (props) => {


  const [projects, setProjecs] = useState([]);
  const [state, setState] = useState({
    loading: false
  })

  // function to handle download submit
  const downloadPDF = (file) => {
    window.open(`${process.env.REACT_APP_DATABASEURL}${file}`);
    console.log(file)
  }

  useEffect(() => {

    async function fetchData() {
      setState({ ...state, loading: true })
      try {
        const token = localStorage.getItem("token")
        const res = await instance
          .get(`${process.env.REACT_APP_DATABASEURL}/project`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          })
        setState({ ...state, loading: false })
        setProjecs(res.data.data.rows)




      } catch (e) {
        console.log(e);
      }
    }
    fetchData();
    //eslint-disable-line
  }, [])


  const project = projects.filter((e) => e.id === Number(props.match.params.id))
  console.log(project)


  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                  <CardTitle className="mb-4">PROJECTS</CardTitle>
                  {state.loading ? (<div class="spinner-border" role="status">
                    <span class="sr-only">Loading...</span>
                  </div>) :
                    <div>
                      {project.map((x, key) => (

                        <div className="table-responsive">
                          <table className="table table-centered table-nowrap mb-0">
                            <thead className="thead-light">
                              <tr>
                                <th>Project Topic</th>
                                <td>
                                  <Link to="#" className="text-body font-weight-bold">
                                    {" "}
                                    {x.order.topic}{" "}
                                  </Link>{" "}
                                </td>

                              </tr>
                              <tr>
                                <th>Assign To</th>
                                <td>{x.User.first_name} {x.User.last_name}</td>
                              </tr>
                              <tr>
                                <th>Date</th>
                                <td><Moment format="YYYY/MM/DD">{x.order.createdAt}</Moment></td>
                              </tr>
                              <tr>
                                <th>Academic Level</th>
                                <td>{x.order.academic_level}</td>
                              </tr>
                              <tr>
                                <th>Urgency</th>
                                <td>{x.order.urgency}</td>
                              </tr>
                            </thead>
                            <tbody>
                            </tbody>
                          </table>
                          <button
                            className="btn btn-primary waves-effect waves-light"
                            type="button"
                          onClick={() => { downloadPDF(x.order.additional_materials) }}
                          >
                            <i class="mdi mdi-download font-size-16 align-middle me-2"></i>
                            {" "}Download pdf
                              </button>
                        </div>
                      ))}
                    </div>
                  }

                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>

    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Orderpage));
