		<Route
		{...rest}
		//   findWriterExam
		render={(props) => (
			<Layout>
				<Component {...props} />
			</Layout>
		)}
	/>
	)

	return (
		<>
			{ grantPermission([ROLES.WRITER]) && showRoutes}
			{
				!grantPermission([ROLES.WRITER]) && (
					<Route
						{...rest}
						render={(props) => (
							<Redirect to={{ pathname: "/login", state: { from: props.location } }} />
						)}

					/>

				)
			}
		</>
	);
}

export default withRouter(stateWrapper(Writermiddleware));
