import React, { useEffect, useState } from "react";
import {  Container, Row, Col, Card, CardBody, CardTitle } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from 'react-moment'


const Orderpage = (props) => {


  const[orders, setOrders] = useState([]);
  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}order`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setOrders(res.data.data.rows)




    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    //eslint-disable-line
  }, [])


  const order = orders.filter((e) => e.id === Number(props.match.params.id))


  return (
    <React.Fragment>
        <div className="page-content">
        <Container fluid>
        <Row>
        <Col lg="12">
        <Card>
         <CardBody>
           <CardTitle className="mb-4">ORDER</CardTitle>
           {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):
                  (
                    <div>
                         {order.map((x, key) => (
           <div className="table-responsive">
            <table className="table table-centered table-nowrap mb-0">
              <thead className="thead-light">
                <tr>
                  <th>Project Topic</th>
                  <td>
                      <Link to="#" className="text-body font-weight-bold">
                        {" "}
                        {x.topic}{" "}
                      </Link>{" "}
                    </td>

                </tr>
                <tr>
                    <th>Status</th>
                    <td>{x.status}</td>
                </tr>
                <tr>
                    <th>Total Amount</th>
                    <td>{x.amount}</td>
                </tr>
                <tr>
                    <th>Academic Level</th>
                    <td>{x.academic_level}</td>
                </tr>
                <tr>
                    <th>Urgency</th>
                    <td>{x.urgency}</td>
                </tr>
                <tr>
                    <th>Created At</th>
                    <td><Moment format="YYYY/MM/DD">{x.createdAt}</Moment> </td>
                </tr>

              </thead>
              <tbody>
              </tbody>
            </table>
          </div>
          ))}
                    </div>
                  )}

         </CardBody>
       </Card>
        </Col>
        </Row>
        </Container>
    </div>

    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Orderpage));
