import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from "react-moment";


const ProjectList = (props) => {

  const[projects, setProjecs] = useState([]);
  const[state, setState] = useState({
    loading: false
  })


  useEffect(() => {
    setState({...state, loading: true})
    console.log(props?.getWritersListOfProjects())
    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}project`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setProjecs(res.data.data.rows)
        console.log(res?.data?.data?.rows)



    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    //eslint-disable-line
  }, [])



  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <CardTitle className="mb-4">ALL PROJECTS</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):(
                        <div className="table-responsive">
                        <table className="table table-centered table-nowrap mb-0">
                          <thead className="thead-light">
                            <tr>
                              <th style={{ width: "20px" }}>
                                <div className="custom-control custom-checkbox">
                                  <input
                                    type="checkbox"
                                    className="custom-control-input"
                                    id="customCheck1"
                                  />
                                  <label
                                    className="custom-control-label"
                                    htmlFor="customCheck1"
                                  >
                                    &nbsp;
                                  </label>
                                </div>
                              </th>
                              <th>Project Topic</th>
                              <th>Date</th>
                              <th>Urgency</th>
                              <th>{" "}</th>
                            </tr>
                          </thead>
                          <tbody>
                            {projects.map((project, key) => (
                              <tr key={"_tr_" + key}>
                                <td>
                                  <div className="custom-control custom-checkbox">
                                    <input
                                      type="checkbox"
                                      className="custom-control-input"
                                      id={project.count}
                                    />
                                    <label
                                      className="custom-control-label"
                                      htmlFor={project.count}
                                    >
                                      &nbsp;
                                    </label>
                                  </div>
                                </td>
                                <td>
                                  <Link to="#" className="text-body font-weight-bold">
                                    {" "}
                                    {project.order.topic}{" "}
                                  </Link>{" "}
                                </td>
                                <td><Moment format="YYYY/MM/DD">{project.order.createdAt}</Moment> </td>
                                <td>{project.order.urgency}</td>
                                <td>
                                  <Link to={"/wprojectspage/" + project.id}>
                                    <Button
                                    color="primary"
                                    className="btn btn-primary waves-effect waves-light"
                                        >
                                    {" "}
                                        View Projects
                                    </Button>
                                  </Link>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                  )}

        </CardBody>
      </Card>

    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(ProjectList));
