import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  NavItem,
  NavLink,
  Label,
  Input,
  Form,
  FormGroup,
} from "reactstrap";
import { Link } from "react-router-dom";

// availity-reactstrap-validation
import { AvForm, AvField, Button } from "availity-reactstrap-validation";
import instance from "../../../helpers/axiosly";



import * as _ from "lodash";
import stateWrapper from "../../../containers/provider";



// Pages Components
// import LatestTranaction from "./LatestTranaction";

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";

//i18n
import { withNamespaces } from "react-i18next";

const CreateService = (props) => {

  const [state, setState] = useState({
    name: "",
    price: "",
    loading: false
  });

  const[updateService, setUpdateService] = useState("");




  useEffect(() => {

    async function fetchData(){
        try {
          const token = localStorage.getItem("token")
          const res = await instance
            .get(`${process.env.REACT_APP_DATABASEURL}/services`, {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            })
            setState({...state, loading: false})
            let currentServices = res.data.data.rows

            let currentService = currentServices.filter((e) => e.id === Number(props.match.params.id))

           console.log(currentService)

          if(props.match.params.id){
            setUpdateService("update")
              setState({
              ...state,
              name: currentService.map(e => {return (e.name)}),
              price: currentService.map(e => {return (e.price)})
              })
          } else {
              setState({
              ...state,
              name: '',
              price: ''
            })
            }

        } catch (e) {
          console.log(e);
        }
        }
        fetchData();


    //eslint-disable-line
  }, [])




  async function handleSubmit(e) {
    setState({
      loading: true,
    });
    const token = localStorage.getItem("token")
    e.preventDefault()
    try {

      await instance
          .post(`${process.env.REACT_APP_DATABASEURL}/services`,
            {
              name: state.name,
              price: state.price
            }, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }).then((response) => {
              console.log(response.data.data)})
              .catch((e) => console.log(e));


    }
     catch (e) {
      console.log(e);
    }

     setState({
              name: "",
              price: "",
              loading: false
            })

            props.history.push({pathname:"/services", state:{status: 'created'}});
}

  async function handleUpdate(e){
    setState({
      loading: true,
    });
    const token = localStorage.getItem("token")
    e.preventDefault()
    let id = Number(props.match.params.id)
    console.log(id)
    try {
      console.log(state.name.toString())
    await instance
        .put(`${process.env.REACT_APP_DATABASEURL}/services/${id}`, {
          name: state.name.toString(),
          price: state.price.toString()
        }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }).then((response) => {
            console.log(response.data.data)})
            .catch((e) => console.log(e));


      }
        catch (e) {
        console.log(e);
      }

      setState({
        name: "",
        price: "",
        loading: false
      })

      props.history.push("/services");
  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          {/* Render Breadcrumb */}
          <Breadcrumbs
            title={props.t("Services")}
            breadcrumbItem={props.t("Create Service")}
          />

          <Row>
            <Col lg="12">
              <Card>
                <CardBody>
                {updateService === "" ? <CardTitle>CREATE SERVICE</CardTitle> :
                    <CardTitle>UPDATE SERVICE</CardTitle>
                  }
                    <form onSubmit={handleSubmit}>
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <Label>
                            Service
                          </Label>
                          <Input
                            value={state.name}
                            onChange={(e) =>
                              setState({ ...state, name: e.target.value })
                            }
                            type="text"
                            className="form-control"

                          />
                        </FormGroup>
                      </Col>

                      <Col lg="6">
                        <FormGroup>
                          <Label>
                            Price
                          </Label>
                          <Input
                            value={state.price}
                            onChange={(e) =>
                              setState({ ...state, price: e.target.value })
                            }
                            type="text"
                            className="form-control"

                          />
                        </FormGroup>
                      </Col>
                    </Row>
                     {updateService === "" ? (
                       <div className=" ">
                         {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                         type="submit"
                         className="btn btn-primary waves-effect waves-light mx-2"
                         type="submit"
                       >
                         Post Service
                       </button>
                         )}

                       </div>
                     ) : (
                      <div>
                        {state.loading ? (
                           <div
                           className="spinner-grow text-primary m-1"
                           role="status"
                         >
                           {/* <span className="sr-only">Loading...</span> */}
                         </div>
                         ): (
                          <button
                        className="btn btn-primary waves-effect waves-light mx-2"
                        type="submit"
                        onClick={handleUpdate}
                      >
                        Update Service
                      </button>
                         )}

                    </div>
                     )}



                  </form>


                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>

);
};

export default withNamespaces()(stateWrapper(CreateService));
