import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link, useLocation } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from "react-moment";



const ServiceList = (props) => {

  const[services, setServices] = useState([]);

  const[state, setState] = useState({
    loading: false,
    alert: ''
  })

  const location = useLocation()
  useEffect(() => {
    setState({...state, loading: true})


      /* const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/services`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }))*/
        let data = props.masterStore.fetchService(props);
        console.log(data)
        setState({...state, loading: false})
        setServices(data)
     /* props.serviceStore.fetchService();

      let serviceData = await props.serviceStore.state.sessionData.service;
      console.log(serviceData)
      setServices(serviceData)*/


   /* if(location.state.status){
      setState({...state, alert: location.state})
      setTimeout(
        function() {
          location.state.status = undefined
        }
      , 3000)
    }*/




    //eslint-disable-line
  }, [])

  function handleDelete(id){

     props.serviceStore.deleteService(id);

  }


  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <div className="d-flex align-items-center justify-content-between">
          <CardTitle className="mb-4">ALL SERVICES</CardTitle>
            <Link to={"/createservice"}>
                <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2 mb-2"
                         >
                     {" "}
                         Create Service
                </Button>
            </Link>
          </div>
          {state.alert == "created" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Success !
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>) : (
         <div className="table-responsive">
         <table className="table table-centered table-nowrap mb-0">
           <thead className="thead-light">
             <tr>
               <th style={{ width: "20px" }}>
                 <div className="custom-control custom-checkbox">
                   <input
                     type="checkbox"
                     className="custom-control-input"
                     id="customCheck1"
                   />
                   <label
                     className="custom-control-label"
                     htmlFor="customCheck1"
                   >
                     &nbsp;
                   </label>
                 </div>
               </th>
               <th>Services</th>
               <th>Price</th>
               <th>Created</th>
               <th>Updated</th>
               <th>{" "}</th>
               <th>{" "}</th>
             </tr>
           </thead>
           <tbody>
             {services && services.map((service, key) => (
               <tr key={"_tr_" + key}>
                 <td>
                   <div className="custom-control custom-checkbox">
                     <input
                       type="checkbox"
                       className="custom-control-input"
                       id={service.id}
                     />
                     <label
                       className="custom-control-label"
                       htmlFor={service.id}
                     >
                       &nbsp;
                     </label>
                   </div>
                 </td>
                 <td>
                   <Link to="#" className="text-body font-weight-bold">
                     {" "}
                     {service.name}{" "}
                   </Link>{" "}
                 </td>
                 <td>{service.price}</td>
                 <td><Moment format="YYYY/MM/DD">{service.createdAt}</Moment></td>
                 <td><Moment format="YYYY/MM/DD">{service.updatedAt}</Moment></td>
                 <td>
                     <Link to={"/createservice/" + service.id}>
                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                         >
                     {" "}
                         <i className="bx bx-edit"></i>
                     </Button>
                     </Link>

                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                     onClick={() => handleDelete(service.id)}
                         >
                     {" "}
                      <i className="bx bx-trash"></i>
                     </Button>

                 </td>
               </tr>
             ))}
           </tbody>
         </table>
       </div>
      )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};



export default (withNamespaces()(stateWrapper(ServiceList)));
