	function SuperAdminmiddleware({
		component: Component, layout: Layout, roles, ...rest
	  }) {
		return (
		  <>
			{ grantPermission([ROLES.SUPERADMIN]) && (
			<Route
			  {...rest}
			  render={(props) => (
				<Layout>
				  <Component {...props} />
				</Layout>
			  )}
			/>
			)}
			{
			  !grantPermission([ROLES.SUPERADMIN]) && (
				<Route
				{...rest}
				render={(props) => (
					<Redirect  to={{ pathname: "/login", state: { from: props.location } }} />
				)}

				/>

			  )
			}
		  </>
		);
	  }

export default withRouter(SuperAdminmiddleware);
