import { Container } from "unstated";
import instance from "../helpers/axiosly";
import { LOCAL_STORAGE_SIGNIN_KEY } from '../App.constants'

// Get token from local storage
const token = localStorage.getItem("token");

class WithdrawalContainer extends Container {
  constructor() {
    super();
    this.state = {
      sessionData: {
        serviceList: [],
      },
    };
  }

//Function to request withdrawal by the writer

requestWithdrawal = (bearertoken, amount) => {
return new Promise((resolve, reject) => {
    instance.post(`${process.env.REACT_APP_DATABASEURL}/withdrawal`,
    {amount: amount},
    {
        headers: {
            Authorization: `Bearer ${bearertoken}`
        }

    }).then((response) => {
        console.log(resolve(response));
        console.log(response)
        resolve(response);
        // return response
    }).catch((e) => {
        console.log(e);
        // console.log(this.response)


        reject(e)
        console.log(reject('ok'))

    });

})
}

}
export { WithdrawalContainer };
