import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, CardBody, CardTitle, Modal, ModalHeader, ModalBody, ModalFooter, Media, Table } from "reactstrap";

// Pages Components
import { Link } from "react-router-dom";
import instance from "../../helpers/axiosly";
import stateWrapper from "../../containers/provider";
import Moment from "react-moment";


//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";


//i18n
import { withNamespaces } from 'react-i18next';

const ProjectNotification = (props) => {

    const [projects, setProjecs] = useState([]);
    const [state, setState] = useState({
        loading: false
    })

    useEffect(() => {
        setState({ ...state, loading: true })
        console.log(props?.writerStore?.getWritersListOfProjects())
        async function fetchData() {
            try {
                let userData = await props.userStore.fetchUser(props);
                console.log(userData,);
                const token = localStorage.getItem("token")
                const res = await props?.writerStore?.getWritersListOfProjects(token)
                setState({ ...state, loading: false })
                setProjecs(res.data.data.rows)
                console.log(res?.data?.data?.rows)
            } catch (e) {
                console.log(e);
            }
        }
        fetchData();
    }, [])

    return (
        <React.Fragment>
            <div className="page-content">
                <Container fluid>

                    {/* Render Breadcrumb */}
                    <Breadcrumbs title={props.t('ProjectNotification')} breadcrumbItem={props.t('All ProjectNotification')} />

                    <Row>
                        <Col lg="12">
                            <Card>
                                <CardBody>
                                    <CardTitle className="mb-4">ALL PROJECTS</CardTitle>
                                    {state.loading ? (<div class="spinner-border" role="status">
                                        <span class="sr-only">Loading...</span>
                                    </div>) : (
                                        <div className="table-responsive">
                                            <table className="table table-centered table-nowrap mb-0">
                                                <thead className="thead-light">
                                                    <tr>
                                                        <th style={{ width: "20px" }}>
                                                            <div className="custom-control custom-checkbox">
                                                                <input
                                                                    type="checkbox"
                                                                    className="custom-control-input"
                                                                    id="customCheck1"
                                                                />
                                                                <label
                                                                    className="custom-control-label"
                                                                    htmlFor="customCheck1"
                                                                >
                                                                    &nbsp;
                                                                </label>
                                                            </div>
                                                        </th>
                                                        <th>Project Topic</th>
                                                        <th>Urgency</th>
                                                        <th>Actions</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {/* <button
                            className="btn btn-primary waves-effect waves-light"
                            type="button"
                          onClick={() => { downloadFile(x.order.additional_materials) }}
                          >
                            <i class="mdi mdi-download font-size-16 align-middle me-2"></i>
                            {" "}Download pdf
                              </button> */}
                                                </tbody>
                                            </table>
                                        </div>
                                    )}

                                </CardBody>
                            </Card>

                        </Col>
                    </Row>
                </Container>
            </div>
        </React.Fragment>
    );
}

export default withNamespaces()(stateWrapper(ProjectNotification));
