import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button, Container, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import Moment from 'react-moment'
import Pagination from './Pagination'

const Users = (props) => {

  const[users, setUsers] = useState([])
  const[currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(10)
  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {
    let mounted = true;
    setState({...state, loading: true})
    async function fetchData(){

    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setUsers(res.data.data.rows)
    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    return () => mounted = false;
    //eslint-disable-line
  }, [])

  const writers = users.filter(user => user.RoleId === 3)

  //get current page
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOffirstUser = indexOfLastUser - usersPerPage;
  const currentWriter = writers.slice(indexOffirstUser, indexOfLastUser);
  //change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  return (
    <React.Fragment>
         <div className="page-content">
                    <Container fluid>

                        {/* Render Breadcrumb */}
                        <Breadcrumbs title={props.t('My Users')} breadcrumbItem={props.t('Writer')} />

                        <Row>
                            <Col lg="12">
                                <Card>
        <CardBody>
          <CardTitle className="mb-4">Users</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>): (
         <div className="table-responsive">
         <table className="table table-centered table-nowrap mb-0">
           <thead className="thead-light">
             <tr>
               <th style={{ width: "20px" }}>
                 <div className="custom-control custom-checkbox">
                   <input
                     type="checkbox"
                     className="custom-control-input"
                     id="customCheck1"
                   />
                   <label
                     className="custom-control-label"
                     htmlFor="customCheck1"
                   >
                     &nbsp;
                   </label>
                 </div>
               </th>
               <th>User ID</th>
               <th>Created At</th>
               <th>Name</th>
               <th>Email</th>
               <th>Role</th>
             </tr>
           </thead>
           <tbody>
             {currentWriter.map((writer, key) => (
               <tr key={"_tr_" + key}>
                 <td>
                   <div className="custom-control custom-checkbox">
                     <input
                       type="checkbox"
                       className="custom-control-input"
                       id={writer.id}
                     />
                     <label
                       className="custom-control-label"
                       htmlFor={writer.id}
                     >
                       &nbsp;
                     </label>
                   </div>
                 </td>
                 <td>
                   <Link to="#" className="text-body font-weight-bold">
                     {" "}
                     {writer.id}{" "}
                   </Link>{" "}
                 </td>
                 <td><Moment format="YYYY/MM/DD">{writer.createdAt}</Moment></td>
                 <td>{writer.first_name} {writer.last_name}</td>
                 <td>{writer.email}</td>
                 <td>Writer</td>
               </tr>
             ))}
           </tbody>
         </table>
         <Pagination usersPerPage={usersPerPage} totalUser={users.length} paginate={paginate}/>
       </div>

      )}

        </CardBody>
      </Card>
                            </Col>
                        </Row>
                    </Container>
                </div>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(Users));
