import { Container } from "unstated";
import instance from "../helpers/axiosly";
import { LOCAL_STORAGE_SIGNIN_KEY } from '../App.constants'

// Get token from local storage
const token = localStorage.getItem("token");

class MasterContainer extends Container {
  constructor() {
    super();
    this.state = {
      sessionData: {
        service: [],

      },
    };
  }

/**
 *
 * State manager for Services
 *
* */

// Function to get list of services
  fetchService = () => {
    instance
      .get(`${process.env.REACT_APP_DATABASEURL}/services`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }).then((response) => {
        const data = response.data.data.rows
        console.log("services here ", data)

        this.setState({
          sessionData: {
            service: data
          },

        });
        console.log(this.state.sessionData.service)
        return {};
      }).catch((e) => {
        console.log(e);
      });

  };

// function to delete service
  deleteService = (id) => {
    console.log("delete services function ")

    console.log(id)
    instance
      .delete(`${process.env.REACT_APP_DATABASEURL}/services/${id}`, {

        headers: {
          Authorization: `Bearer ${token}`,
        },

      }).catch((e) => {
        console.log(e);
      });

  }
}



export { MasterContainer };
