import React, { useEffect, useState, useCallback } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link, useLocation, useHistory } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from "react-moment";



const ServiceList = (props) => {

  //const[services, setServices] = useState(null);

  const[state, setState] = useState({
    loading: false,
    alert: ''
  })

  const location = useLocation();
  const history = useHistory();

  useEffect(() => {
    setState({...state, loading: true});
    let serviceData, userData;
    userData = props.userStore.fetchUser(props);

    async function fetchData(){
      const res = await props.masterStore.fetchService(
        userData.access_token
      );
      if(res.data.status == 'Success'){
        setTimeout(function(){
          setState({...state, loading: false})
        }, 2000)
      }
    }
    fetchData();


   if(location.state && location.state.status) {
    setState({...state, alert: location.state})

    //delete location state
     history.replace({
       pathname: location.pathname, state: {}
     })
   }

    //eslint-disable-line
  }, [])


  //delete service
  const handleDelete = async (id) =>{

     const res = await props.masterStore.deleteService(id);

     if(res.data.status == 'Success'){
      setState({...state, alert: 'deleted'})
      setTimeout(function(){
       setState({...state, alert: ''})
     }, 2000)
     }
     window.location.reload()
  }

  //servicelist
  let services = props.masterStore.state.sessionData.serviceList;

  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <div className="d-flex align-items-center justify-content-between">
          <CardTitle className="mb-4">ALL SERVICES</CardTitle>
            <Link to={"/createservice"}>
                <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2 mb-2"
                         >
                     {" "}
                         Create Service
                </Button>
            </Link>
          </div>
          {state.alert.status == "created" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> New Service created
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}

        {state.alert.status == "updated" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Service updated
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
           {state.alert == "deleted" ? (
            <div>
              <div
                className="alert alert-info alert-dismissible fade show"
                role="alert"
              >
                <i className="mdi mdi-alert-circle-outline mr-2"></i> Service deleted
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

            </div>
          ) : (
            ""
          )}
          {state.loading ? (<div class="spinner-border" role="status">
      <span class="sr-only">Loading...</span>
      </div>) : (
         <div className="table-responsive">
         <table className="table table-centered table-nowrap mb-0">
           <thead className="thead-light">
             <tr>
              <th>#</th>
               <th>Services</th>
               <th>Price</th>
               <th>Created</th>
               <th>Updated</th>
               <th>{" "}</th>
               <th>{" "}</th>
             </tr>
           </thead>
           <tbody>
             {services && services.map((service, key) => (
               <tr key={"_tr_" + key}>
                 <th scope="row">{key + 1}</th>
                 <td>
                   <Link to="#" className="text-body font-weight-bold">
                     {" "}
                     {service.name}{" "}
                   </Link>{" "}
                 </td>
                 <td>{service.price}</td>
                 <td><Moment format="YYYY/MM/DD">{service.createdAt}</Moment></td>
                 <td><Moment format="YYYY/MM/DD">{service.updatedAt}</Moment></td>
                 <td>
                     <Link to={"/createservice/" + service.id}>
                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                         >
                     {" "}
                         <i className="bx bx-edit"></i>
                     </Button>
                     </Link>

                     <Button
                     color="primary"
                     className="btn btn-primary waves-effect waves-light mx-2"
                     onClick={() => handleDelete(service.id)}
                         >
                     {" "}
                      <i className="bx bx-trash"></i>
                     </Button>

                 </td>
               </tr>
             ))}
           </tbody>
         </table>
       </div>
      )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};



export default (withNamespaces()(stateWrapper(ServiceList)));
