import React, { useEffect, useState, useRef } from "react";
import {  Container, Row, Col, Card, CardBody, Label, CardTitle, Input, Form, FormGroup, CustomInput, ListGroup,ListGroupItem, ButtonGroup, Button, Jumbotron, Progress } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";
import Moment from 'react-moment'
import AvForm from "availity-reactstrap-validation/lib/AvForm";



const Question = ({data, onAnswerUpdate, numberOfQuestion, activeQuestion, onSetActiveQuestion, onSetStep, setShowPrev}) => {

    const [selected, setSelected] = useState('');
    const [error, setError] = useState('')
    const radioWrapper = useRef()

    const changeHandler = (e) => {
        setSelected(e.target.value);

        if(error) {
            setError('')
        }
    }

    const nextClickHandler = (e) => {
        if(selected === '') {
            return setError('Please select one option');
        }
        onAnswerUpdate(prevState => [...prevState, {
            q: data.question , a: selected, s: data.score
        }]);
        setSelected('')
        if(activeQuestion < numberOfQuestion - 1) {
            onSetActiveQuestion(activeQuestion + 1)
        } else {
            onSetStep(3)
        }
    }

    const PrevHandler = (e) => {
        setSelected('')
        onAnswerUpdate([]);
        onSetActiveQuestion(activeQuestion - 1)
    }

    useEffect(() => {
        const findcheckedInput = radioWrapper.current.querySelector('input:checked');
        if(findcheckedInput) {
            findcheckedInput.checked = false;
        }

    }, [data])
  return (
    <React.Fragment>

        <Card>
         <CardBody>
           <h4 className="card-title mb-4">QUESTION </h4>
           <div id="basic-pills-wizard" className="twitter-bs-wizard">

            <div className="mb-2">
                <Row>
              <Col lg="12">
                <h5 className="text-info">
                  Question {activeQuestion + 1} of {numberOfQuestion}
                </h5>
                <p>{data.question}</p>
              </Col>
            </Row>
             <Row>
             <Col lg="12">
               <form>
                 <div ref={radioWrapper}>
                 {data.options.map((option, id) => (
                    <label className="radio" key={id}>
                    <input type="radio" name="answer" value={option}  onChange={changeHandler}/>
                       {option}
                    </label>
                   ))}
                </div>

               </form>
               {error && <div className="text-danger">{error}</div>}
                <Button color="primary" className="btn btn-primary waves-effect waves-light mx-2" onClick={nextClickHandler}>
                    Submit</Button>
                {activeQuestion > 0 &&
                <Button color="primary" className="btn btn-primary waves-effect waves-light mx-2" onClick={PrevHandler}>
                    Back
                </Button>}

             </Col>
           </Row>
           <br/>
            </div>

                {/* questions && <button
                      type="submit"
                      className="btn btn-warning waves-effect waves-light mt-4"
                    >
                      Submit
                </button>*/}
           </div>


         </CardBody>
       </Card>


    </React.Fragment>
  );
};


export default withNamespaces()(stateWrapper(Question));
