import { Container } from "unstated";
import instance from "../helpers/axiosly";
import {LOCAL_STORAGE_SIGNIN_KEY} from '../App.constants'


class ServiceContainer extends Container {
  constructor() {
    super();
    this.state = {
      sessionData: {
        service: []
      },
    };
  }
// 

  fetchService = () => {

    const token = localStorage.getItem("token")
         instance
          .get(`${process.env.REACT_APP_DATABASEURL}/services`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }).then((response) => {
              const data = response.data.data.rows

            this.setState({
                sessionData: {
                    service: data
                },

              });
              console.log(this.state.sessionData.service)
              return {};
        }).catch((e) => {
            console.log(e);
          });

  };

  deleteService = (id) => {
    const token = localStorage.getItem("token")
    console.log(id)
       instance
        .delete(`${process.env.REACT_APP_DATABASEURL}/services/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }).catch((e) => {
            console.log(e);
          });

  }



}

export { ServiceContainer };
