import React, { useState, useEffect } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import moment from "moment";

const RecentClient = (props) => {


  const [state, setState] = useState({
    clientList: [],
    loading: false
  });

  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })

        let users = res.data.data.rows
        const client = users.filter(user => user.RoleId === 2)

        const lastList = client.slice(0, 2)

          setState({
            ...state,
            clientList: lastList,
            loading: false
          });

    } catch (e) {
      console.log(e);
    }
    }
    fetchData();

    //eslint-disable-line
  }, [])


  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <CardTitle className="mb-4">RECENT CLIENT</CardTitle>
          {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):
          (
            <div>
              <Link
            className="btn btn-primary waves-effect waves-light mb-2"
            to="/client"
          >
            View Clients
          </Link>
          <div className="table-responsive">
            <table className="table table-centered table-nowrap mb-0">
              <thead className="thead-light">
                <tr>
                  <th style={{ width: "20px" }}>
                    <div className="custom-control custom-checkbox">
                      <input
                        type="checkbox"
                        className="custom-control-input"
                        id="customCheck1"
                      />
                      <label
                        className="custom-control-label"
                        htmlFor="customCheck1"
                      >
                        &nbsp;
                      </label>
                    </div>
                  </th>
                  <th>User ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Created At</th>
                </tr>
              </thead>
              <tbody>
                {state.clientList.map((client, key) => (
                  <tr key={"_tr_" + key}>
                    <td>
                      <div className="custom-control custom-checkbox">
                        <input
                          type="checkbox"
                          className="custom-control-input"
                          id={client.id}
                        />
                        <label
                          className="custom-control-label"
                          htmlFor={client.id}
                        >
                          &nbsp;
                        </label>
                      </div>
                    </td>
                    <td>
                      <Link to="#" className="text-body font-weight-bold">
                        {" "}
                        {client.id}{" "}
                      </Link>{" "}
                    </td>
                    <td>{client.first_name} {client.first_name}</td>
                    <td>{client.email}</td>
                    <td>{moment(client.createdAt).format("MMMM Do YYYY")}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
            </div>
          )}
        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default RecentClient;
