import React, { useEffect, useState } from "react";
import { Card, CardBody, CardTitle, Badge, Button } from "reactstrap";
import { Link, Redirect } from "react-router-dom";
import instance from "../../../helpers/axiosly";
import stateWrapper from "../../../containers/provider";
import { withNamespaces } from "react-i18next";


const OrderList = (props) => {

  const[orders, setOrders] = useState([]);
  const[users, setUsers] = useState([])
  const[state, setState] = useState({
    loading: false
  })

  useEffect(() => {
    setState({...state, loading: true})
    async function fetchData(){ 
    try {
      const token = localStorage.getItem("token")
      const res = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/order`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setState({...state, loading: false})
        setOrders(res.data.data.rows)
        console.log()


      const result = await instance
        .get(`${process.env.REACT_APP_DATABASEURL}/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        setUsers(result.data.data.rows)

    } catch (e) {
      console.log(e);
    }
    }
    fetchData();
    //eslint-disable-line
  }, [])



  return (
    <React.Fragment>

      <Card>
        <CardBody>
          <CardTitle className="mb-4">ALL ORDERS</CardTitle>
         {state.loading ? (<div class="spinner-border" role="status">
                  <span class="sr-only">Loading...</span>
                  </div>):(
                    <div className="table-responsive">
                    <table className="table table-centered table-nowrap mb-0">
                      <thead className="thead-light">
                        <tr>
                          <th style={{ width: "20px" }}>
                            <div className="custom-control custom-checkbox">
                              <input
                                type="checkbox"
                                className="custom-control-input"
                                id="customCheck1"
                              />
                              <label
                                className="custom-control-label"
                                htmlFor="customCheck1"
                              >
                                &nbsp;
                              </label>
                            </div>
                          </th>
                          <th>Project Topic</th>
                          <th>Status</th>
                          <th>Name</th>
                          <th>Total Amount</th>
                          <th>{" "}</th>
                        </tr>
                      </thead>
                      <tbody>
                        {orders.map((order, key) => (
                          <tr key={"_tr_" + key}>
                            <td>
                              <div className="custom-control custom-checkbox">
                                <input
                                  type="checkbox"
                                  className="custom-control-input"
                                  id={order.id}
                                />
                                <label
                                  className="custom-control-label"
                                  htmlFor={order.id}
                                >
                                  &nbsp;
                                </label>
                              </div>
                            </td>
                            <td>
                              <Link to="#" className="text-body font-weight-bold">
                                {" "}
                                {order.topic}{" "}
                              </Link>{" "}
                            </td>
                            <td>{order.status}</td>
                            <td>Name</td>
                            <td>{order.amount}</td>
                            <td>
                              <Link to={`/orderpage/${order.id}`}>
                                <Button
                                color="primary"
                                className="btn btn-primary waves-effect waves-light"
                                    >
                                {" "}
                                    View Orders
                                </Button>
                              </Link>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                  )}

        </CardBody>
      </Card>
    </React.Fragment>
  );
};

export default withNamespaces()(stateWrapper(OrderList));
